steps.command:
$gpu = Get-CimInstance Win32_VideoController | Sort-Object AdapterRAM -Descending | Select-Object -First 1 Name;
$gpuName = ($gpu.Name | Out-String).Trim();
if ($gpuName -match 'NVIDIA|GeForce|RTX|GTX') {
  $paths = @(
    "$env:SystemRoot\System32\nvcplui.exe",
    "$env:ProgramFiles\NVIDIA Corporation\Control Panel Client\nvcplui.exe"
  );
  foreach ($p in $paths) { if (Test-Path $p) { Start-Process $p; return } }
  Start-Process ms-settings:display;
} elseif ($gpuName -match 'AMD|Radeon|RX ') {
  $paths = @(
    "$env:ProgramFiles\AMD\CNext\CNext\RadeonSoftware.exe",
    "$env:ProgramFiles\AMD\CNext\CNext\AMDRadeonSoftware.exe",
    "$env:ProgramFiles\AMD\CNext\CNext\AMDSoftware.exe"
  );
  foreach ($p in $paths) { if (Test-Path $p) { Start-Process $p; return } }
  Start-Process ms-settings:display;
} else {
  Start-Process ms-settings:display;
}

$path = Join-Path $env:LOCALAPPDATA 'VALORANT\Saved\Config'
if (Test-Path $path) {
  Start-Process explorer.exe $path
} else {
  Write-Output 'Valorant config folder not found at standard location.'
}

$path = Join-Path $env:LOCALAPPDATA 'FortniteGame\Saved\Config\WindowsClient\GameUserSettings.ini'
if (Test-Path $path) {
  Start-Process notepad.exe $path
} else {
  Write-Output 'Fortnite GameUserSettings.ini not found. Launch Fortnite once, then close it and try again.'
}

$base = Join-Path $env:LOCALAPPDATA 'VALORANT\Saved\Config'
if (!(Test-Path -LiteralPath $base)) {
  Write-Output 'Valorant config root not found at expected path.';
  exit 0
}

$files = Get-ChildItem -Path $base -Recurse -Filter 'GameUserSettings.ini' -ErrorAction SilentlyContinue
if (-not $files) {
  Write-Output 'No GameUserSettings.ini found under Valorant config.';
  exit 0
}

foreach ($f in $files) {
  try {
    $content = Get-Content -LiteralPath $f.FullName -Raw -ErrorAction SilentlyContinue
    if (-not $content) { continue }

    $replacements = @{
      'ResolutionSizeX='     = 'ResolutionSizeX=1280'
      'ResolutionSizeY='     = 'ResolutionSizeY=960'
      'DesiredScreenWidth='  = 'DesiredScreenWidth=1280'
      'DesiredScreenHeight=' = 'DesiredScreenHeight=960'
    }

    foreach ($k in $replacements.Keys) {
      $replacement = $replacements[$k]
      if ($content -match [regex]::Escape($k)) {
        $content = [regex]::Replace($content, [regex]::Escape($k) + '\d+', $replacement)
      } else {
        $content += "`n" + $replacement
      }
    }

    Set-Content -LiteralPath $f.FullName -Value $content -Encoding ASCII
  } catch {
    Write-Output ('Failed to touch Valorant config: {0}' -f $f.FullName)
  }
}

Write-Output 'Applied 1280x960-style stretched preset for Valorant (config files updated where found).'

$paths = @()
$paths += Join-Path $env:USERPROFILE 'Saved Games\Counter-Strike 2'
$paths += Join-Path $env:USERPROFILE 'Documents\Counter-Strike 2'
$existing = $paths | Where-Object { Test-Path -LiteralPath $_ }
if (-not $existing -or $existing.Count -eq 0) {
  Write-Output 'Could not find a CS2 config folder in common locations.';
  exit 0
}
$target = $existing[0]
Start-Process explorer.exe $target
Write-Output ('Opened CS2 config folder: {0}' -f $target)
