steps.command:
# Start helper if not already running
try {
  $exists = Get-CimInstance Win32_Process -Filter "Name='powershell.exe'" -ErrorAction SilentlyContinue |
    Where-Object { $_.CommandLine -like "*timer-helper.ps1*" }
  if($exists){ return }
} catch {}
$helper = Join-Path $PSScriptRoot "timer-helper.ps1"
Start-Process -FilePath "powershell.exe" -ArgumentList ("-NoProfile -ExecutionPolicy Bypass -WindowStyle Hidden -File `"" + $helper + "`" -Resolution 5000") -WindowStyle Hidden

$name = 'DiagTrack'
Write-Output ("[Hardcore] Disabling service: DiagTrack")
try {
  $svc = Get-Service -Name $name -ErrorAction SilentlyContinue
  if ($null -ne $svc) {
    if ($svc.Status -eq 'Running') {
      Stop-Service -Name $name -Force -ErrorAction SilentlyContinue
    }
    Set-Service -Name $name -StartupType Disabled -ErrorAction SilentlyContinue
  } else {
    Write-Output ("Service not found: DiagTrack")
  }
} catch {
  Write-Output ("Failed to disable service DiagTrack: " + $_.Exception.Message)
}

