try {
  powercfg -duplicatescheme e9a42b02-d5df-448d-aa00-03f14749eb61 | Out-Null
} catch {}
try {
  powercfg -setactive e9a42b02-d5df-448d-aa00-03f14749eb61
  Write-Output "Set active power plan to Ultimate Performance."
} catch {
  Write-Output ("Failed to set Ultimate Performance plan: " + $_.Exception.Message)
}

$path = 'HKLM:\SYSTEM\CurrentControlSet\Control\PriorityControl'
try {
  New-Item -Path $path -Force | Out-Null
  New-ItemProperty -Path $path -Name 'Win32PrioritySeparation' -Value 26 -PropertyType DWord -Force | Out-Null
  Write-Output 'Set Win32PrioritySeparation=0x1A (foreground favor, long quantum).'
} catch {
  Write-Output ("Failed to set Win32PrioritySeparation: " + $_.Exception.Message)
}

try {
  New-Item -Path 'HKCU:\System\GameConfigStore' -Force | Out-Null
  New-ItemProperty -Path 'HKCU:\System\GameConfigStore' -Name 'GameDVR_Enabled' -Value 0 -PropertyType DWord -Force | Out-Null
  New-ItemProperty -Path 'HKCU:\System\GameConfigStore' -Name 'GameDVR_FSEBehaviorMode' -Value 2 -PropertyType DWord -Force | Out-Null
} catch {}
try {
  New-Item -Path 'HKLM:\SOFTWARE\Policies\Microsoft\Windows\GameDVR' -Force | Out-Null
  New-ItemProperty -Path 'HKLM:\SOFTWARE\Policies\Microsoft\Windows\GameDVR' -Name 'AllowGameDVR' -Value 0 -PropertyType DWord -Force | Out-Null
} catch {}
Write-Output 'Disabled Game DVR / Game Bar via registry.'

$path = 'HKLM:\SYSTEM\CurrentControlSet\Control\Session Manager\Memory Management'
try {
  New-Item -Path $path -Force | Out-Null
