  New-ItemProperty -Path $path -Name 'FeatureSettingsOverrideMask' -Value 3 -PropertyType DWord -Force | Out-Null
  Write-Output 'Set FeatureSettingsOverride + Mask to 3 (disable mitigations). Reboot required.'
} catch {
  Write-Output ("Failed to set mitigations override: " + $_.Exception.Message)
}

$path = 'HKLM:\SOFTWARE\Microsoft\Windows NT\CurrentVersion\Multimedia\SystemProfile'
