try {
  $dev = Get-PnpDevice -FriendlyName '*High precision event timer*' -ErrorAction SilentlyContinue
  if ($dev) {
    Disable-PnpDevice -InstanceId $dev.InstanceId -Confirm:$false -ErrorAction SilentlyContinue
    Write-Output 'Disabled High Precision Event Timer device.'
  } else {
    Write-Output 'HPET device not found.'
  }
} catch {
  Write-Output ("Failed to adjust HPET device: " + $_.Exception.Message)
}

try {
  bcdedit /set disabledynamictick yes
  Write-Output 'Set disabledynamictick = yes (reboot required).'
} catch {
  Write-Output ("Failed to set disabledynamictick: " + $_.Exception.Message)
}

try {
  $ifaces = Get-NetIPInterface -AddressFamily IPv4 -ErrorAction SilentlyContinue
  foreach ($iface in $ifaces) {
    $id = $iface.InterfaceIndex
    $reg = "HKLM:\SYSTEM\CurrentControlSet\Services\Tcpip\Parameters\Interfaces\{0}" -f $iface.InterfaceGuid
    New-Item -Path $reg -Force | Out-Null
    New-ItemProperty -Path $reg -Name 'TcpNoDelay' -Value 1 -PropertyType DWord -Force | Out-Null
    New-ItemProperty -Path $reg -Name 'TcpAckFrequency' -Value 1 -PropertyType DWord -Force | Out-Null
  }
  Write-Output 'Applied TcpNoDelay=1 and TcpAckFrequency=1 on all IPv4 interfaces.'
} catch {
  Write-Output ("Failed to adjust Nagle settings: " + $_.Exception.Message)
}

