steps.command:
$gpu = Get-CimInstance Win32_VideoController | Sort-Object AdapterRAM -Descending | Select-Object -First 1 Name;
$gpuName = ($gpu.Name | Out-String).Trim();
if ($gpuName -match 'NVIDIA|GeForce|RTX|GTX') {
  $paths = @(
    "$env:SystemRoot\System32\nvcplui.exe",
    "$env:ProgramFiles\NVIDIA Corporation\Control Panel Client\nvcplui.exe"
  );
  foreach ($p in $paths) { if (Test-Path $p) { Start-Process $p; return } }
  Start-Process ms-settings:display;
} elseif ($gpuName -match 'AMD|Radeon|RX ') {
  $paths = @(
    "$env:ProgramFiles\AMD\CNext\CNext\RadeonSoftware.exe",
    "$env:ProgramFiles\AMD\CNext\CNext\AMDRadeonSoftware.exe",
    "$env:ProgramFiles\AMD\CNext\CNext\AMDSoftware.exe"
  );
  foreach ($p in $paths) { if (Test-Path $p) { Start-Process $p; return } }
  Start-Process ms-settings:display;
} else {
  Start-Process ms-settings:display;
}

$gpu = Get-CimInstance Win32_VideoController | Sort-Object AdapterRAM -Descending | Select-Object -First 1 Name;
$gpuName = ($gpu.Name | Out-String).Trim();
if ($gpuName -match 'NVIDIA|GeForce|RTX|GTX') {
  $paths = @(
    "$env:SystemRoot\System32\nvcplui.exe",
    "$env:ProgramFiles\NVIDIA Corporation\Control Panel Client\nvcplui.exe"
  );
  foreach ($p in $paths) { if (Test-Path $p) { Start-Process $p; return } }
  Start-Process ms-settings:display;
} elseif ($gpuName -match 'AMD|Radeon|RX ') {
  $paths = @(
    "$env:ProgramFiles\AMD\CNext\CNext\RadeonSoftware.exe",
    "$env:ProgramFiles\AMD\CNext\CNext\AMDRadeonSoftware.exe",
    "$env:ProgramFiles\AMD\CNext\CNext\AMDSoftware.exe"
  );
  foreach ($p in $paths) { if (Test-Path $p) { Start-Process $p; return } }
  Start-Process ms-settings:display;
} else {
  Start-Process ms-settings:display;
}

Write-Output "=== FALCON DRIVER LAB: GPU DRIVER HEALTH SCAN ==="
Write-Output ""

# Detect main GPU vendor
$gpuVendor = "Unknown"
try {
  $gpus = Get-CimInstance Win32_VideoController | Where-Object { $_.AdapterRAM -gt 0 }
  if ($gpus) {
    $primary = $gpus | Sort-Object AdapterRAM -Descending | Select-Object -First 1
    $name = $primary.Name
    if ($name -match "NVIDIA") { $gpuVendor = "NVIDIA" }
    elseif ($name -match "AMD" -or $name -match "Radeon") { $gpuVendor = "AMD" }
    elseif ($name -match "Intel") { $gpuVendor = "Intel" }
    Write-Output ("Primary GPU: {0} (Vendor: {1})" -f $name, $gpuVendor)
  } else {
    Write-Output "No VRAM GPUs found in Win32_VideoController."
  }
} catch {
  Write-Output "Failed to query GPU from Win32_VideoController."
}

Write-Output ""

# Common driver folders and cache paths
$paths = @()

# DirectX shader cache
$dxCache = Join-Path $env:LOCALAPPDATA "D3DSCache"
$paths += @{ Name = "DirectX Shader Cache"; Path = $dxCache }

if ($gpuVendor -eq "NVIDIA") {
  $paths += @{ Name = "NV Shader Cache"; Path = Join-Path $env:LOCALAPPDATA "NVIDIA\DXCache" }
  $paths += @{ Name = "NV GLCache"; Path = Join-Path $env:LOCALAPPDATA "NVIDIA\GLCache" }
  $paths += @{ Name = "NV Profile Store (DRS)"; Path = "C:\ProgramData\NVIDIA Corporation\Drs" }
} elseif ($gpuVendor -eq "AMD") {
  $paths += @{ Name = "AMD DX Cache"; Path = Join-Path $env:LOCALAPPDATA "AMD\DxCache" }
  $paths += @{ Name = "AMD GL Cache"; Path = Join-Path $env:LOCALAPPDATA "AMD\GLCache" }
} elseif ($gpuVendor -eq "Intel") {
  $paths += @{ Name = "Intel Shader Cache"; Path = Join-Path $env:LOCALAPPDATA "Intel\ShaderCache" }
}

Write-Output "Cache / profile paths:"
foreach ($p in $paths) {
  $exists = Test-Path $p.Path
  Write-Output ("  - {0}: {1} [{2}]" -f $p.Name, $p.Path, ($exists ? "Exists" : "Missing"))
}

Write-Output ""
Write-Output "Driver package overview (PnP signed drivers referencing GPU vendor):"
try {
  if ($gpuVendor -ne "Unknown") {
    $drv = Get-WmiObject Win32_PnPSignedDriver -ErrorAction SilentlyContinue | Where-Object {
      $_.DeviceName -match $gpuVendor -or $_.DriverProviderName -match $gpuVendor
    }
    foreach ($d in $drv | Select-Object -First 10) {
      Write-Output ("  {0} | Version: {1} | Date: {2}" -f $d.DeviceName, $d.DriverVersion, $d.DriverDate)
    }
    if (-not $drv) {
      Write-Output "  (No matching PnP signed drivers found for vendor.)"
    }
  } else {
    Write-Output "  (GPU vendor unknown; skipping PnP driver scan.)"
  }
} catch {
  Write-Output "  Failed to query PnP signed drivers."
}

Write-Output ""
Write-Output "NVIDIA-specific checks (if NVIDIA detected):"
if ($gpuVendor -eq "NVIDIA") {
  # nvidia-smi basic check
  try {
    $nv = & nvidia-smi -q -d DRIVER 2>$null
    if ($nv) {
      Write-Output "  nvidia-smi driver info:"
      $nv.Split([Environment]::NewLine) | Where-Object { $_ -match "Driver Version" -or $_ -match "Driver Model" } | ForEach-Object {
        Write-Output ("    {0}" -f $_.Trim())
      }
    } else {
      Write-Output "  nvidia-smi not available or failed."
    }
  } catch {
    Write-Output "  Failed to run nvidia-smi for driver info."
  }

  # NV Telemetry / GeForce Experience tasks hint
  try {
    $tasks = schtasks /Query /FO LIST /V 2>$null
    if ($tasks) {
      $nvTasks = $tasks -split "[\r\n]+" | Where-Object { $_ -match "NVIDIA" -or $_ -match "NvTelemetry" -or $_ -match "GeForce" }
      if ($nvTasks) {
        Write-Output "  NVIDIA-related scheduled task entries detected (telemetry/experience may be installed)."
      } else {
        Write-Output "  No obvious NVIDIA scheduled tasks detected."
      }
    }
  } catch {
    Write-Output "  Failed to query scheduled tasks for NVIDIA entries."
  }
}

Write-Output ""
Write-Output "Summary:"
Write-Output "  - If caches are large/old and stuttery, you can clear them using the Falcon cache cleanup action."
Write-Output "  - For broken drivers, use vendor clean-installer tools; Falcon will not fake a driver reinstall."
Write-Output ""
Write-Output "=== END OF GPU DRIVER HEALTH SCAN ==="

Write-Output "=== FALCON DRIVER LAB: GPU/DX CACHE CLEANUP ==="
Write-Output ""
Write-Output "This will attempt to clear common GPU and DirectX shader caches for the primary GPU vendor."
Write-Output "It does NOT uninstall or reinstall drivers."
Write-Output ""

$gpuVendor = "Unknown"
try {
  $gpus = Get-CimInstance Win32_VideoController | Where-Object { $_.AdapterRAM -gt 0 }
  if ($gpus) {
    $primary = $gpus | Sort-Object AdapterRAM -Descending | Select-Object -First 1
    $name = $primary.Name
    if ($name -match "NVIDIA") { $gpuVendor = "NVIDIA" }
    elseif ($name -match "AMD" -or $name -match "Radeon") { $gpuVendor = "AMD" }
    elseif ($name -match "Intel") { $gpuVendor = "Intel" }
    Write-Output ("Primary GPU: {0} (Vendor: {1})" -f $name, $gpuVendor)
  }
} catch {}

$paths = @()
$paths += @{ Name = "DirectX Shader Cache"; Path = Join-Path $env:LOCALAPPDATA "D3DSCache" }

if ($gpuVendor -eq "NVIDIA") {
  $paths += @{ Name = "NV DXCache"; Path = Join-Path $env:LOCALAPPDATA "NVIDIA\DXCache" }
  $paths += @{ Name = "NV GLCache"; Path = Join-Path $env:LOCALAPPDATA "NVIDIA\GLCache" }
} elseif ($gpuVendor -eq "AMD") {
  $paths += @{ Name = "AMD DxCache"; Path = Join-Path $env:LOCALAPPDATA "AMD\DxCache" }
  $paths += @{ Name = "AMD GLCache"; Path = Join-Path $env:LOCALAPPDATA "AMD\GLCache" }
} elseif ($gpuVendor -eq "Intel") {
  $paths += @{ Name = "Intel Shader Cache"; Path = Join-Path $env:LOCALAPPDATA "Intel\ShaderCache" }
}

foreach ($p in $paths) {
  Write-Output ("Clearing {0}: {1}" -f $p.Name, $p.Path)
  try {
    if (Test-Path $p.Path) {
      Get-ChildItem $p.Path -Recurse -Force -ErrorAction SilentlyContinue | Remove-Item -Force -Recurse -ErrorAction SilentlyContinue
      Write-Output "  -> Cleared."
    } else {
      Write-Output "  -> Path does not exist; nothing to clear."
    }
  } catch {
    Write-Output ("  -> Failed to clear: {0}" -f $_.Exception.Message)
  }
}

Write-Output ""
Write-Output "Recommended: Reboot or at least restart GPU-intensive apps so they rebuild caches cleanly."
Write-Output "=== END OF GPU/DX CACHE CLEANUP ==="

$name = 'GraphicsPerfSvc'
Write-Output ("[Hardcore] Disabling additional service: GraphicsPerfSvc")
try {
  $svc = Get-Service -Name $name -ErrorAction SilentlyContinue
  if ($svc) {
    if ($svc.Status -eq 'Running') {
      Stop-Service -Name $name -Force -ErrorAction SilentlyContinue
    }
    Set-Service -Name $name -StartupType Disabled -ErrorAction SilentlyContinue
  } else {
    Write-Output ("Service not found: GraphicsPerfSvc")
  }
} catch {
  Write-Output ("Failed to disable service GraphicsPerfSvc: " + $_.Exception.Message)
}

$path = 'HKLM:\SYSTEM\CurrentControlSet\Control\GraphicsDrivers'
try {
  New-Item -Path $path -Force | Out-Null
  New-ItemProperty -Path $path -Name 'HwSchMode' -Value 2 -PropertyType DWord -Force | Out-Null
  Write-Output 'Set HwSchMode=2 (Hardware Accelerated GPU Scheduling ON). Reboot required.'
} catch {
  Write-Output ("Failed to set HAGS: " + $_.Exception.Message)
}

$path = 'HKLM:\SYSTEM\CurrentControlSet\Control\GraphicsDrivers'
try {
  New-Item -Path $path -Force | Out-Null
  New-ItemProperty -Path $path -Name 'HwSchMode' -Value 1 -PropertyType DWord -Force | Out-Null
  Write-Output 'Set HwSchMode=1 (Hardware Accelerated GPU Scheduling OFF). Reboot required.'
} catch {
  Write-Output ("Failed to set HAGS: " + $_.Exception.Message)
}

$path = 'HKLM:\SYSTEM\CurrentControlSet\Control\GraphicsDrivers'
try {
  New-Item -Path $path -Force | Out-Null
  New-ItemProperty -Path $path -Name 'TdrDelay' -Value 10 -PropertyType DWord -Force | Out-Null
  New-ItemProperty -Path $path -Name 'TdrDdiDelay' -Value 10 -PropertyType DWord -Force | Out-Null
  Write-Output 'Set TdrDelay and TdrDdiDelay to 10 seconds.'
} catch {
  Write-Output ("Failed to adjust TDR delay: " + $_.Exception.Message)
}

Write-Output "=== FALCON SYSTEM SCAN ==="
Write-Output ""

# OS
try {
  $os = Get-CimInstance Win32_OperatingSystem
  Write-Output "OS: $($os.Caption) (Version $($os.Version), Build $($os.BuildNumber))"
} catch {
  Write-Output "OS: <failed to query>"
}

# CPU
try {
  $cpu = Get-CimInstance Win32_Processor
  Write-Output ""
  Write-Output "CPU:"
  Write-Output "  Name: $($cpu.Name)"
  Write-Output "  Manufacturer: $($cpu.Manufacturer)"
  Write-Output "  Cores (physical): $($cpu.NumberOfCores)"
  Write-Output "  Threads (logical): $($cpu.NumberOfLogicalProcessors)"
  Write-Output "  Max Clock (MHz): $($cpu.MaxClockSpeed)"
} catch {
  Write-Output "CPU: <failed to query>"
}

# GPU
try {
  Write-Output ""
  Write-Output "GPU(s):"
  $gpus = Get-CimInstance Win32_VideoController
  foreach ($g in $gpus) {
    $vramGB = ""
    try {
      if ($g.AdapterRAM -gt 0) {
        $vramGB = [math]::Round($g.AdapterRAM / 1GB, 2)
      }
    } catch {}
    Write-Output ("  - {0} (VRAM: {1} GB, Driver: {2})" -f $g.Name, ($vramGB -as [string]), $g.DriverVersion)
  }
} catch {
  Write-Output "GPU: <failed to query>"
}

# RAM
try {
  Write-Output ""
  $mem = Get-CimInstance Win32_PhysicalMemory
  $totalBytes = ($mem | Measure-Object -Property Capacity -Sum).Sum
  $totalGB = [math]::Round($totalBytes / 1GB, 2)
  Write-Output "Memory:"
  Write-Output ("  Total: {0} GB" -f $totalGB)
  $speeds = $mem | Select-Object -First 4 Manufacturer, Speed, ConfiguredClockSpeed, Capacity
  $idx = 0
  foreach ($m in $speeds) {
    $idx++
    Write-Output ("  Stick {0}: {1} MHz (Configured: {2} MHz) - {3} GB" -f $idx, $m.Speed, $m.ConfiguredClockSpeed, [math]::Round($m.Capacity/1GB,2))
  }
} catch {
  Write-Output "Memory: <failed to query>"
}

# Storage
try {
  Write-Output ""
  Write-Output "Storage:"
  $drives = Get-PhysicalDisk
  foreach ($d in $drives) {
    Write-Output ("  - {0}: {1} ({2}, {3})" -f $d.FriendlyName, $d.MediaType, $d.BusType, $d.Size)
  }
} catch {
  Write-Output "Storage: <failed to query>"
}

# Motherboard / BIOS
try {
  Write-Output ""
  $bb = Get-CimInstance Win32_Baseboard
  $bios = Get-CimInstance Win32_BIOS
  Write-Output "Motherboard:"
  Write-Output ("  {0} {1}" -f $bb.Manufacturer, $bb.Product)
  Write-Output "BIOS:"
  Write-Output ("  Version: {0}" -f $bios.SMBIOSBIOSVersion)
  Write-Output ("  ReleaseDate: {0}" -f $bios.ReleaseDate)
} catch {
  Write-Output "Motherboard/BIOS: <failed to query>"
}

# Power plan
try {
  Write-Output ""
  $active = (powercfg /getactivescheme 2>$null)
  Write-Output "Power Plan:"
  Write-Output ("  Active: {0}" -f $active)
} catch {
  Write-Output "Power Plan: <failed to query>"
}

# GraphicsDrivers (HAGS / TDR)
try {
  Write-Output ""
  $gd = Get-ItemProperty -Path 'HKLM:\SYSTEM\CurrentControlSet\Control\GraphicsDrivers' -ErrorAction SilentlyContinue
  if ($gd) {
    Write-Output "Graphics Drivers (core flags):"
    Write-Output ("  HwSchMode (HAGS): {0}" -f $gd.HwSchMode)
    Write-Output ("  TdrDelay: {0}" -f $gd.TdrDelay)
    Write-Output ("  TdrDdiDelay: {0}" -f $gd.TdrDdiDelay)
  } else {
    Write-Output "Graphics Drivers (core flags): <no key>"
  }
} catch {
  Write-Output "Graphics Drivers (core flags): <failed to query>"
}

# Device Guard / VBS
try {
  Write-Output ""
  $dg = Get-CimInstance Win32_DeviceGuard -ErrorAction SilentlyContinue
  if ($dg) {
    Write-Output "Security / Device Guard:"
    Write-Output ("  SecurityServicesConfigured: {0}" -f ($dg.SecurityServicesConfigured -join ','))
    Write-Output ("  SecurityServicesRunning: {0}" -f ($dg.SecurityServicesRunning -join ','))
  } else {
    Write-Output "Security / Device Guard: <not available>"
  }
} catch {
  Write-Output "Security / Device Guard: <failed to query>"
}

# Network adapters
try {
  Write-Output ""
  Write-Output "Network Adapters:"
  $nics = Get-NetAdapter -Physical -ErrorAction SilentlyContinue
  foreach ($n in $nics) {
    Write-Output ("  - {0} | {1} | Status: {2} | LinkSpeed: {3}" -f $n.Name, $n.InterfaceDescription, $n.Status, $n.LinkSpeed)
  }
} catch {
  Write-Output "Network Adapters: <failed to query>"
}

Write-Output ""
Write-Output "=== End of Falcon System Scan ==="

Write-Output "=== FALCON THERMAL ENGINE: OVERVIEW & SCAN ==="
Write-Output ""

# CPU basic info and current clocks (approx)
try {
  $cpu = Get-CimInstance Win32_Processor
  Write-Output ("CPU: {0}" -f $cpu.Name)
  Write-Output ("  Cores: {0}, Threads: {1}" -f $cpu.NumberOfCores, $cpu.NumberOfLogicalProcessors)
  Write-Output ("  MaxClockSpeed (MHz): {0}" -f $cpu.MaxClockSpeed)
  # Current clock (single sample)
  try {
    $cur = Get-Counter '\Processor Information(_Total)\Processor Frequency' -ErrorAction SilentlyContinue
    if ($cur) {
      $mhz = [math]::Round($cur.CounterSamples[0].CookedValue,0)
      Write-Output ("  Current Frequency (MHz): {0}" -f $mhz)
    }
  } catch {}
} catch {
  Write-Output "CPU: <failed to query>"
}

Write-Output ""

# Attempt to read ACPI thermal zones (not always populated)
try {
  $tz = Get-WmiObject -Namespace root/wmi -Class MSAcpi_ThermalZoneTemperature -ErrorAction SilentlyContinue
  if ($tz) {
    Write-Output "ACPI Thermal Zones (may not reflect real CPU/GPU sensors):"
    foreach ($t in $tz) {
      $tempC = ($t.CurrentTemperature - 2732) / 10.0
      Write-Output ("  {0}: {1:N1} C" -f $t.InstanceName, $tempC)
    }
  } else {
    Write-Output "ACPI Thermal Zones: <none or unsupported on this system>"
  }
} catch {
  Write-Output "ACPI Thermal Zones: <failed to query>"
}

Write-Output ""

# Try NVIDIA GPU temperature via nvidia-smi if present
try {
  $nv = & nvidia-smi --query-gpu=temperature.gpu,clocks.sm,power.draw --format=csv,noheader 2>$null
  if ($nv) {
    Write-Output "NVIDIA GPU (via nvidia-smi):"
    foreach ($line in $nv) {
      Write-Output ("  {0}" -f $line.Trim())
    }
  } else {
    Write-Output "NVIDIA GPU: nvidia-smi not available or no NVIDIA GPU detected."
  }
} catch {
  Write-Output "NVIDIA GPU: failed to run nvidia-smi."
}

Write-Output ""

# Power plan cooling policy
try {
  Write-Output "Power Plan Cooling Policy (AC):"
  $guid_scheme = "SCHEME_CURRENT"
  $sub_processor = "SUB_PROCESSOR"
  $cool_guid = "94D3A615-A899-4AC5-AE2B-E4D8F634367F" # SYSTEM_COOLING_POLICY
  $coolRaw = powercfg /QUERY $guid_scheme $sub_processor $cool_guid 2>$null
  if ($coolRaw) {
    Write-Output $coolRaw
  } else {
    Write-Output "  (Could not query SYSTEM_COOLING_POLICY via powercfg.)"
  }
} catch {
  Write-Output "Cooling Policy: <failed to query>"
}

Write-Output ""

# PowerThrottling registry state (global)
try {
  $ptKey = "HKLM:\SYSTEM\CurrentControlSet\Control\Power\PowerThrottling"
  if (Test-Path $ptKey) {
    $pt = Get-ItemProperty $ptKey -ErrorAction SilentlyContinue
    if ($pt.PSObject.Properties.Name -contains "PowerThrottlingOff") {
      Write-Output ("PowerThrottlingOff (global): {0}" -f $pt.PowerThrottlingOff)
    } else {
      Write-Output "PowerThrottlingOff: <not set, using defaults>"
    }
  } else {
    Write-Output "PowerThrottling key not found (likely using defaults)."
  }
} catch {
  Write-Output "PowerThrottling: <failed to query>"
}

Write-Output ""
Write-Output "Tip: Thermal Engine focuses on keeping cooling policy 'Active' and reducing OS-side throttling where safe."
Write-Output "      For full sensor-level monitoring (per-core temps), use vendor tools alongside Falcon."
Write-Output ""
Write-Output "=== END OF THERMAL OVERVIEW & SCAN ==="
