steps.command:
$dir = Join-Path $env:ProgramData "FalconOptimizer"
New-Item -ItemType Directory -Force -Path $dir | Out-Null
$fn = Join-Path $dir "latency_overrides.json"
if (-not (Test-Path -LiteralPath $fn)) {
@'
{
  "scheduler": {
    "Win32PrioritySeparation": "0x26"
  },
  "timer": {
    "resolution_us": 5000
  },
  "bcdedit": {
    "disabledynamictick": "yes",
    "useplatformtick": "yes",
    "useplatformclock": "no"
  }
}
'@ | Out-File -LiteralPath $fn -Encoding utf8
}
Start-Process notepad.exe $fn

$ifs = Get-ChildItem "HKLM:\SYSTEM\CurrentControlSet\Services\Tcpip\Parameters\Interfaces" -ErrorAction SilentlyContinue
foreach($k in $ifs){ 
  New-ItemProperty -Path $k.PSPath -Name "TcpNoDelay" -Value 1 -PropertyType DWord -Force | Out-Null
  New-ItemProperty -Path $k.PSPath -Name "TcpAckFrequency" -Value 1 -PropertyType DWord -Force | Out-Null
}
Write-Output "Set on interfaces"

$u = (powercfg /list | Select-String -Pattern "Ultimate Performance").ToString()
if($u){
  $guid = ($u -split '\s+')[3]
  powercfg /setactive $guid
  Write-Output "Activated $guid"
}else{
  Write-Output "Not present"
}

$ifs = Get-ChildItem "HKLM:\SYSTEM\CurrentControlSet\Services\Tcpip\Parameters\Interfaces" -ErrorAction SilentlyContinue
foreach($k in $ifs){ 
  New-ItemProperty -Path $k.PSPath -Name "TcpNoDelay" -Value 1 -PropertyType DWord -Force | Out-Null
  New-ItemProperty -Path $k.PSPath -Name "TcpAckFrequency" -Value 1 -PropertyType DWord -Force | Out-Null
}
Write-Output "Set on interfaces"

$u = (powercfg /list | Select-String -Pattern "Ultimate Performance").ToString()
if($u){
  $guid = ($u -split '\s+')[3]
  powercfg /setactive $guid
  Write-Output "Activated $guid"
}else{
  Write-Output "Not present"
}

Write-Output "=== FALCON REGISTRY LAB: OVERVIEW ANALYZER ==="
Write-Output ""
Write-Output "Scanning a few common bloat / history zones in HKCU to estimate how dirty Explorer history is."
Write-Output ""

$zones = @(
  @{ Name = "RunMRU";        Path = "HKCU:\Software\Microsoft\Windows\CurrentVersion\Explorer\RunMRU" },
  @{ Name = "RecentDocs";    Path = "HKCU:\SOFTWARE\Microsoft\Windows\CurrentVersion\Explorer\RecentDocs" },
  @{ Name = "OpenSaveMRU";   Path = "HKCU:\SOFTWARE\Microsoft\Windows\CurrentVersion\Explorer\ComDlg32\OpenSavePidlMRU" },
  @{ Name = "TypedPaths";    Path = "HKCU:\Software\Microsoft\Windows\CurrentVersion\Explorer\TypedPaths" },
  @{ Name = "WordWheelQuery";Path = "HKCU:\SOFTWARE\Microsoft\Windows\CurrentVersion\Explorer\WordWheelQuery" }
)

foreach ($z in $zones) {
  $name = $z.Name
  $path = $z.Path
  try {
    if (Test-Path $path) {
      $key  = Get-Item $path -ErrorAction SilentlyContinue
      $vals = $key.Property | Where-Object { $_ -ne "(default)" }
      $sub  = Get-ChildItem $path -ErrorAction SilentlyContinue
      $valCount = $vals.Count
      $subCount = if ($sub) { $sub.Count } else { 0 }
      Write-Output ("{0}: Values={1}, Subkeys={2} ({3})" -f $name, $valCount, $subCount, $path)
    } else {
      Write-Output ("{0}: Not present ({1})" -f $name, $path)
    }
  } catch {
    Write-Output ("{0}: Error reading {1}: {2}" -f $name, $path, $_.Exception.Message)
  }
}

Write-Output ""
Write-Output "These zones are where Explorer and dialogs store run history, open/save history, and search suggestions."
Write-Output "Cleaning them can reset Explorer/file-dialog history and slightly reduce registry bloat; it will not magically add FPS."
Write-Output ""
Write-Output "=== END OF REGISTRY LAB OVERVIEW ==="

Write-Output "=== FALCON REGISTRY LAB: CLEAN EXPLORER RUN HISTORY ==="
Write-Output ""
Write-Output "This will clean Run dialog history, typed paths, and search suggestions under HKCU Explorer keys."
Write-Output "Impact: resets Explorer/Run history for this user only. No core OS components are removed."
Write-Output ""

$targets = @(
  "HKCU:\Software\Microsoft\Windows\CurrentVersion\Explorer\RunMRU",
  "HKCU:\Software\Microsoft\Windows\CurrentVersion\Explorer\TypedPaths",
  "HKCU:\SOFTWARE\Microsoft\Windows\CurrentVersion\Explorer\WordWheelQuery"
)

foreach ($t in $targets) {
  try {
    if (Test-Path $t) {
      Remove-Item -Path $t -Recurse -Force -ErrorAction SilentlyContinue
      Write-Output ("Cleared: {0}" -f $t)
    } else {
      Write-Output ("Skipped (not present): {0}" -f $t)
    }
  } catch {
    Write-Output ("Failed to clear {0}: {1}" -f $t, $_.Exception.Message)
  }
}

Write-Output ""
Write-Output "Explorer will recreate needed keys as you use it. A logoff/logon or Explorer restart can refresh behavior."
Write-Output "=== END OF EXPLORER RUN HISTORY CLEAN ==="

Write-Output "=== FALCON REGISTRY LAB: CLEAN OPEN/SAVE MRU ==="
Write-Output ""
Write-Output "This will clean RecentDocs and OpenSave dialog MRU entries under HKCU."
Write-Output "Impact: clears per-user 'recent files' lists and file-type dialog history. No core OS components are removed."
Write-Output ""

$targets = @(
  "HKCU:\SOFTWARE\Microsoft\Windows\CurrentVersion\Explorer\RecentDocs",
  "HKCU:\SOFTWARE\Microsoft\Windows\CurrentVersion\Explorer\ComDlg32\OpenSavePidlMRU"
)

foreach ($t in $targets) {
  try {
    if (Test-Path $t) {
      Remove-Item -Path $t -Recurse -Force -ErrorAction SilentlyContinue
      Write-Output ("Cleared: {0}" -f $t)
    } else {
      Write-Output ("Skipped (not present): {0}" -f $t)
    }
  } catch {
    Write-Output ("Failed to clear {0}: {1}" -f $t, $_.Exception.Message)
  }
}

Write-Output ""
Write-Output "A logoff/logon or Explorer restart can reset recent-items views after this cleanup."
Write-Output "=== END OF OPEN/SAVE MRU CLEAN ==="
