steps.command:
# Start helper if not already running
try {
  $exists = Get-CimInstance Win32_Process -Filter "Name='powershell.exe'" -ErrorAction SilentlyContinue |
    Where-Object { $_.CommandLine -like "*timer-helper.ps1*" }
  if($exists){ return }
} catch {}
$helper = Join-Path $PSScriptRoot "timer-helper.ps1"
Start-Process -FilePath "powershell.exe" -ArgumentList ("-NoProfile -ExecutionPolicy Bypass -WindowStyle Hidden -File `"" + $helper + "`" -Resolution 5000") -WindowStyle Hidden

try {
  $procs = Get-CimInstance Win32_Process -Filter "Name='powershell.exe'" -ErrorAction SilentlyContinue |
    Where-Object { $_.CommandLine -like "*timer-helper.ps1*" }
  if($procs){
    foreach($p in $procs){
      try { Stop-Process -Id $p.ProcessId -Force -ErrorAction SilentlyContinue } catch {}
    }
  }
} catch {}

$names = @(
 "discord","chrome","msedge","spotify","steam","EpicGamesLauncher","EpicWebHelper",
 "OneDrive","Teams","YourPhone","PhoneExperienceHost","GameBar","GameBarFTServer"
)
foreach($n in $names){
  try {
    $p = Get-Process -Name $n -ErrorAction SilentlyContinue
    if($p){ Stop-Process -Id $p.Id -Force -ErrorAction SilentlyContinue }
  } catch {}
}

$svcs = "XblAuthManager","XblGameSave","XboxGipSvc","XboxNetApiSvc"
foreach($s in $svcs){ 
  $svc = Get-Service -Name $s -ErrorAction SilentlyContinue
  if($svc){ try{ Stop-Service $s -Force -ErrorAction SilentlyContinue }catch{} }
}
Write-Output "Stopped"

$svcs = "XblAuthManager","XblGameSave","XboxGipSvc","XboxNetApiSvc"
foreach($s in $svcs){ 
  $svc = Get-Service -Name $s -ErrorAction SilentlyContinue
  if($svc){ try{ Start-Service $s -ErrorAction SilentlyContinue }catch{} }
}
Write-Output "Attempted restore"

$path = Join-Path $env:LOCALAPPDATA 'VALORANT\Saved\Config'
if (Test-Path $path) {
  Start-Process explorer.exe $path
} else {
  Write-Output 'Valorant config folder not found at standard location.'
}

$path = Join-Path $env:LOCALAPPDATA 'FortniteGame\Saved\Config\WindowsClient\GameUserSettings.ini'
if (Test-Path $path) {
  Start-Process notepad.exe $path
} else {
  Write-Output 'Fortnite GameUserSettings.ini not found. Launch Fortnite once, then close it and try again.'
}

$paths = @()
$paths += Join-Path $env:USERPROFILE 'Saved Games\Counter-Strike 2'
$paths += Join-Path $env:USERPROFILE 'Documents\Counter-Strike 2'
$existing = $paths | Where-Object { Test-Path -LiteralPath $_ }
if (-not $existing -or $existing.Count -eq 0) {
  Write-Output 'Could not find a CS2 config folder in common locations.';
  exit 0
}
$target = $existing[0]
Start-Process explorer.exe $target
Write-Output ('Opened CS2 config folder: {0}' -f $target)

Write-Output "=== FALCON AUDIO LAB: ENGINE & DEVICE SCAN ==="
Write-Output ""

# List sound devices
try {
  $snd = Get-CimInstance Win32_SoundDevice
  Write-Output "Sound Devices:"
  foreach ($s in $snd) {
    Write-Output ("  - {0} | Manufacturer: {1} | Status: {2}" -f $s.Name, $s.Manufacturer, $s.Status)
  }
} catch {
  Write-Output "Sound Devices: <failed to query>"
}

Write-Output ""
Write-Output "Audio Render Endpoints (from MMDevices registry):"
$mmBase = "HKCU:\Software\Microsoft\Windows\CurrentVersion\MMDevices\Audio\Render"
if (Test-Path $mmBase) {
  Get-ChildItem $mmBase -ErrorAction SilentlyContinue | ForEach-Object {
    $devKey = $_.PsPath
    $propKey = Join-Path $devKey "Properties"
    Write-Output ("  Endpoint: {0}" -f $devKey)
    if (Test-Path $propKey) {
      try {
        $p = Get-ItemProperty $propKey -ErrorAction SilentlyContinue
        $friendly = $p.'{a45c254e-df1c-4efd-8020-67d146a850e0},2'  # PKEY_Device_FriendlyName
        $desc     = $p.'{b3f8fa53-0004-438e-9003-51a46e139bfc},6'  # PKEY_AudioEndpoint_FormFactor or similar
        Write-Output ("    Name: {0}" -f $friendly)
        if ($desc) {
          Write-Output ("    Extra: {0}" -f $desc)
        }
      } catch {
        Write-Output "    (Failed to read endpoint properties.)"
      }
    }
    Write-Output ""
  }
} else {
  Write-Output "  MMDevices\\Audio\\Render key not found."
}

Write-Output ""
Write-Output "Note: Audio Lab will focus on disabling enhancements/spatial sound and simplifying the audio stack for lowest latency."
Write-Output ""
Write-Output "=== END OF AUDIO SCAN ==="

Write-Output "=== FALCON AUDIO LAB: DISABLE ENHANCEMENTS & SPATIAL SOUND (GLOBAL) ==="
Write-Output ""
Write-Output "This will attempt to disable global audio enhancements and spatial sound flags where exposed in the registry."
Write-Output "Per-device audio enhancement flags are complex; Falcon focuses on the main global switches."
Write-Output ""

# Global audio enhancements off
try {
  $globalPath = "HKLM:\SOFTWARE\Microsoft\Windows\CurrentVersion\Audio"
  if (-not (Test-Path $globalPath)) {
    New-Item -Path $globalPath -Force | Out-Null
  }
  New-ItemProperty -Path $globalPath -Name "DisableAudioEnhancements" -Value 1 -PropertyType DWord -Force | Out-Null
  Write-Output "Set HKLM\\...\\Audio\\DisableAudioEnhancements = 1"
} catch {
  Write-Output ("Failed to set DisableAudioEnhancements: {0}" -f $_.Exception.Message)
}

# Disable system sounds scheme (optional latency / distraction)
try {
  $schemePath = "HKCU:\AppEvents\Schemes"
  if (Test-Path $schemePath) {
    New-ItemProperty -Path $schemePath -Name "(Default)" -Value ".None" -PropertyType String -Force | Out-Null
    Write-Output "Set system sound scheme to 'No Sounds'."
  } else {
    Write-Output "AppEvents\\Schemes not found under HKCU."
  }
} catch {
  Write-Output ("Failed to adjust system sound scheme: {0}" -f $_.Exception.Message)
}

# Spatial sound hint (cannot robustly disable all vendor-specific implementations)
Write-Output ""
Write-Output "NOTE: Per-device spatial sound (Dolby Atmos, Sonic) usually must be turned off in the Sound control panel / UWP apps."
Write-Output "      Falcon does not attempt to patch every vendor-specific spatial sound setting."
Write-Output ""
Write-Output "A reboot or sign-out/in may be required for all audio changes to fully apply."
Write-Output ""
Write-Output "=== END OF AUDIO ENHANCEMENT DISABLE ==="

Write-Output "=== FALCON DPC LATENCY LAB: GLOBAL DPC/ISR OVERVIEW ==="
Write-Output ""
Write-Output "Note: This is an approximate, OS-level DPC/ISR snapshot. For deep driver-level analysis, tools like LatencyMon are still recommended."
Write-Output ""

try {
  $cpu = Get-Counter '\Processor(_Total)\% DPC Time' -ErrorAction SilentlyContinue
  $isr = Get-Counter '\Processor(_Total)\% Interrupt Time' -ErrorAction SilentlyContinue

  if ($cpu) {
    $dpcVal = [math]::Round($cpu.CounterSamples[0].CookedValue, 3)
    Write-Output ("Current % DPC Time: {0} %" -f $dpcVal)
  } else {
    Write-Output "Could not read % DPC Time counter."
  }

  if ($isr) {
    $isrVal = [math]::Round($isr.CounterSamples[0].CookedValue, 3)
    Write-Output ("Current % Interrupt Time: {0} %" -f $isrVal)
  } else {
    Write-Output "Could not read % Interrupt Time counter."
  }
} catch {
  Write-Output ("Failed to query DPC/ISR counters: {0}" -f $_.Exception.Message)
}

Write-Output ""
Write-Output "Sampling DPC latency trend for 30 samples (~30 seconds)..."
Write-Output ""

$samples = @()
for ($i = 1; $i -le 30; $i++) {
  try {
    $c = Get-Counter '\Processor(_Total)\% DPC Time' -ErrorAction SilentlyContinue
    if ($c) {
      $val = $c.CounterSamples[0].CookedValue
      $samples += $val
      Write-Output ("[{0}/30] % DPC Time: {1:N3}" -f $i, $val)
    } else {
      Write-Output ("[{0}/30] Failed to read DPC counter." -f $i)
    }
  } catch {
    Write-Output ("[{0}/30] Error: {1}" -f $i, $_.Exception.Message)
  }
  Start-Sleep -Seconds 1
}

if ($samples.Count -gt 0) {
  $avg = ($samples | Measure-Object -Average).Average
  $max = ($samples | Measure-Object -Maximum).Maximum
  $stdev = [math]::Sqrt((($samples | ForEach-Object { ($_ - $avg) * ($_ - $avg) }) | Measure-Object -Sum).Sum / $samples.Count)
  Write-Output ""
  Write-Output ("Avg % DPC Time: {0:N3}" -f $avg)
  Write-Output ("Max % DPC Time: {0:N3}" -f $max)
  Write-Output ("StdDev: {0:N3}" -f $stdev)
}

Write-Output ""
Write-Output "Heuristic guidance:"
Write-Output "  - Sustained DPC % close to 0 with low spikes is ideal."
Write-Output "  - High spikes or sustained values may indicate problematic drivers (storage, NIC, audio)."
Write-Output ""
Write-Output "=== END OF DPC LATENCY OVERVIEW ==="

Write-Output "=== FALCON GAMEPACK 2.0: GAME AUTO-DETECT & RECOMMEND ==="
Write-Output ""
Write-Output "Scanning for installed competitive titles and mapping them to Falcon modules..."
Write-Output ""

$results = @()

function Test-GamePath {
  param(
    [string]$Name,
    [string[]]$Paths
  )
  foreach ($p in $Paths) {
    if ([string]::IsNullOrWhiteSpace($p)) { continue }
    try {
      if (Test-Path $p) {
        return @{ Name = $Name; Installed = $true; Path = $p }
      }
    } catch {}
  }
  return @{ Name = $Name; Installed = $false; Path = $null }
}

# Build candidate paths per game

$programFiles      = ${env:ProgramFiles}
$programFilesX86   = ${env:ProgramFiles(x86)}
$localAppData      = ${env:LOCALAPPDATA}
$commonProgramData = ${env:ProgramData}

$games = @()

# Fortnite (Epic)
$fnPaths = @()
$fnPaths += (Join-Path $programFiles "Epic Games\Fortnite")
$fnPaths += (Join-Path $programFilesX86 "Epic Games\Fortnite")
$games += Test-GamePath -Name "Fortnite" -Paths $fnPaths

# Valorant (Riot)
$valPaths = @()
$valPaths += (Join-Path $programFiles "Riot Games\VALORANT")
$valPaths += (Join-Path $programFilesX86 "Riot Games\VALORANT")
$valPaths += (Join-Path $localAppData "VALORANT")
$games += Test-GamePath -Name "Valorant" -Paths $valPaths

# CS2 (Steam)
$steamCommon = @()
$steamCommon += (Join-Path $programFilesX86 "Steam\steamapps\common")
$steamCommon += (Join-Path $programFiles "Steam\steamapps\common")
$cs2Paths = @()
foreach ($s in $steamCommon) {
  if (-not [string]::IsNullOrWhiteSpace($s)) {
    $cs2Paths += (Join-Path $s "Counter-Strike Global Offensive")
    $cs2Paths += (Join-Path $s "Counter-Strike 2")
  }
}
$games += Test-GamePath -Name "CS2" -Paths $cs2Paths

# Apex Legends (Steam / Origin)
$apexPaths = @()
foreach ($s in $steamCommon) {
  if (-not [string]::IsNullOrWhiteSpace($s)) {
    $apexPaths += (Join-Path $s "Apex Legends")
  }
}
$apexPaths += (Join-Path $programFiles "Origin Games\Apex")
$apexPaths += (Join-Path $programFilesX86 "Origin Games\Apex")
$games += Test-GamePath -Name "Apex Legends" -Paths $apexPaths

# Rocket League (Steam / Epic)
$rlPaths = @()
foreach ($s in $steamCommon) {
  if (-not [string]::IsNullOrWhiteSpace($s)) {
    $rlPaths += (Join-Path $s "rocketleague")
  }
}
$rlPaths += (Join-Path $programFiles "Epic Games\rocketleague")
$games += Test-GamePath -Name "Rocket League" -Paths $rlPaths

# GTA V
$gtaPaths = @()
foreach ($s in $steamCommon) {
  if (-not [string]::IsNullOrWhiteSpace($s)) {
    $gtaPaths += (Join-Path $s "Grand Theft Auto V")
  }
}
$gtaPaths += (Join-Path $programFiles "Rockstar Games\Grand Theft Auto V")
$gtaPaths += (Join-Path $programFilesX86 "Rockstar Games\Grand Theft Auto V")
$games += Test-GamePath -Name "GTA V" -Paths $gtaPaths

# League of Legends
$lolPaths = @()
$lolPaths += (Join-Path $programFiles "Riot Games\League of Legends")
$lolPaths += (Join-Path $programFilesX86 "Riot Games\League of Legends")
$games += Test-GamePath -Name "League of Legends" -Paths $lolPaths

# GeForce NOW (cloud)
$gfnPaths = @()
$gfnPaths += (Join-Path $programFiles "NVIDIA Corporation\NVIDIA GeForce NOW")
$gfnPaths += (Join-Path $programFilesX86 "NVIDIA Corporation\NVIDIA GeForce NOW")
$games += Test-GamePath -Name "GeForce NOW" -Paths $gfnPaths

Write-Output "Detected games:"
foreach ($g in $games) {
  if ($g.Installed) {
    Write-Output ("  - {0}: Installed at {1}" -f $g.Name, $g.Path)
  } else {
    Write-Output ("  - {0}: Not detected (using heuristics only)" -f $g.Name)
  }
}

Write-Output ""
Write-Output "=== Falcon GamePack 2.0 Recommendations ==="
Write-Output ""

foreach ($g in $games) {
  if (-not $g.Installed) { continue }

  switch ($g.Name) {
    "Fortnite" {
      Write-Output ">> Fortnite:"
      Write-Output "   - Use: Game Specific  Fortnite"
      Write-Output "   - Combine with: StretchLab, Input Latency Lab, Network Latency Lab, and Driver Lab cache cleanup."
      Write-Output "   - Suggested Falcon mode: Competitive / Extreme (if your hardware and stability are good)."
      Write-Output ""
    }
    "Valorant" {
      Write-Output ">> Valorant:"
      Write-Output "   - Use: Game Specific  Valorant"
      Write-Output "   - Combine with: Input Latency Lab, Network Latency Lab (NIC tuning), DPC Latency Lab."
      Write-Output "   - Suggested Falcon mode: Competitive (avoid fully nuking services Riot might require)."
      Write-Output ""
    }
    "CS2" {
      Write-Output ">> CS2:"
      Write-Output "   - Use: Game Specific  CS2"
      Write-Output "   - Combine with: Driver Lab (cache cleanup), Audio Lab (for clean positional audio), DPC Latency Lab."
      Write-Output "   - Suggested Falcon mode: Competitive or Extreme depending on stability."
      Write-Output ""
    }
    "Apex Legends" {
      Write-Output ">> Apex Legends:"
      Write-Output "   - Use: Game Specific  Apex Legends"
      Write-Output "   - Combine with: Network Latency Lab, Driver Lab cache cleanup, Thermal Engine (if CPU/GPU run hot)."
      Write-Output ""
    }
    "Rocket League" {
      Write-Output ">> Rocket League:"
      Write-Output "   - Use: Game Specific  Rocket League"
      Write-Output "   - Combine with: Input Latency Lab (controller/HID tuning), Network Latency Lab jitter tests."
      Write-Output ""
    }
    "GTA V" {
      Write-Output ">> GTA V:"
      Write-Output "   - Use: Game Specific  GTA V"
      Write-Output "   - Combine with: Thermal Engine (extended sessions), Audio Lab, and moderate debloat."
      Write-Output ""
    }
    "League of Legends" {
      Write-Output ">> League of Legends:"
      Write-Output "   - Use: Game Specific  League of Legends"
      Write-Output "   - Combine with: Network Latency Lab, DPC Latency Lab to keep input + camera panning smooth."
      Write-Output ""
    }
    "GeForce NOW" {
      Write-Output ">> GeForce NOW:"
      Write-Output "   - Use: Game Specific  GeForce NOW"
      Write-Output "   - Combine with: Network Latency Lab, Driver Lab (for GPU encoding stability), and Audio Lab."
      Write-Output ""
    }
  }
}

Write-Output "If a game is not detected but installed in a non-standard path, you can still use its Falcon tab manually."
Write-Output "GamePack 2.0 does not auto-change configs; it gives you a per-game Falcon roadmap based on what it finds."
Write-Output ""
Write-Output "=== END OF GAMEPACK 2.0 AUTO-DETECT ==="

# Start helper if not already running
try {
  $exists = Get-CimInstance Win32_Process -Filter "Name='powershell.exe'" -ErrorAction SilentlyContinue |
    Where-Object { $_.CommandLine -like "*timer-helper.ps1*" }
  if($exists){ return }
} catch {}
$helper = Join-Path $PSScriptRoot "timer-helper.ps1"
Start-Process -FilePath "powershell.exe" -ArgumentList ("-NoProfile -ExecutionPolicy Bypass -WindowStyle Hidden -File `"" + $helper + "`" -Resolution 5000") -WindowStyle Hidden

try {
  powercfg -duplicatescheme e9a42b02-d5df-448d-aa00-03f14749eb61 | Out-Null
} catch {}
try {
  powercfg -setactive e9a42b02-d5df-448d-aa00-03f14749eb61
  Write-Output "Set active power plan to Ultimate Performance."
} catch {
  Write-Output ("Failed to set Ultimate Performance plan: " + $_.Exception.Message)
}

$path = 'HKLM:\SYSTEM\CurrentControlSet\Control\PriorityControl'
try {
  New-Item -Path $path -Force | Out-Null
  New-ItemProperty -Path $path -Name 'Win32PrioritySeparation' -Value 26 -PropertyType DWord -Force | Out-Null
  Write-Output 'Set Win32PrioritySeparation=0x1A (foreground favor, long quantum).'
} catch {
  Write-Output ("Failed to set Win32PrioritySeparation: " + $_.Exception.Message)
}

try {
  New-Item -Path 'HKCU:\System\GameConfigStore' -Force | Out-Null
  New-ItemProperty -Path 'HKCU:\System\GameConfigStore' -Name 'GameDVR_Enabled' -Value 0 -PropertyType DWord -Force | Out-Null
  New-ItemProperty -Path 'HKCU:\System\GameConfigStore' -Name 'GameDVR_FSEBehaviorMode' -Value 2 -PropertyType DWord -Force | Out-Null
} catch {}
try {
  New-Item -Path 'HKLM:\SOFTWARE\Policies\Microsoft\Windows\GameDVR' -Force | Out-Null
  New-ItemProperty -Path 'HKLM:\SOFTWARE\Policies\Microsoft\Windows\GameDVR' -Name 'AllowGameDVR' -Value 0 -PropertyType DWord -Force | Out-Null
} catch {}
Write-Output 'Disabled Game DVR / Game Bar via registry.'

$path = 'HKLM:\SYSTEM\CurrentControlSet\Control\Session Manager\Memory Management'
try {
  New-Item -Path $path -Force | Out-Null
  New-ItemProperty -Path $path -Name 'FeatureSettingsOverride' -Value 3 -PropertyType DWord -Force | Out-Null
  New-ItemProperty -Path $path -Name 'FeatureSettingsOverrideMask' -Value 3 -PropertyType DWord -Force | Out-Null
  Write-Output 'Set FeatureSettingsOverride + Mask to 3 (disable mitigations). Reboot required.'
} catch {
  Write-Output ("Failed to set mitigations override: " + $_.Exception.Message)
}

$path = 'HKLM:\SOFTWARE\Microsoft\Windows NT\CurrentVersion\Multimedia\SystemProfile'
try {
  New-Item -Path $path -Force | Out-Null
  New-ItemProperty -Path $path -Name 'SystemResponsiveness' -Value 10 -PropertyType DWord -Force | Out-Null
  New-ItemProperty -Path $path -Name 'NetworkThrottlingIndex' -Value 0xffffffff -PropertyType DWord -Force | Out-Null
  Write-Output 'Set SystemResponsiveness=10 and NetworkThrottlingIndex=0xffffffff.'
} catch {
  Write-Output ("Failed to adjust MMCSS related keys: " + $_.Exception.Message)
}

try {
  powercfg -setacvalueindex scheme_current sub_processor PROCTHROTTLEMIN 91
  powercfg -setactive scheme_current
  Write-Output 'Set minimum processor performance on AC to 91%.'
} catch {
  Write-Output ("Failed to set processor minimum performance: " + $_.Exception.Message)
}

try {
  powercfg -setacvalueindex scheme_current sub_processor PROCTHROTTLEMIN 92
  powercfg -setactive scheme_current
  Write-Output 'Set minimum processor performance on AC to 92%.'
} catch {
  Write-Output ("Failed to set processor minimum performance: " + $_.Exception.Message)
}

try {
  powercfg -setacvalueindex scheme_current sub_processor PROCTHROTTLEMIN 93
  powercfg -setactive scheme_current
  Write-Output 'Set minimum processor performance on AC to 93%.'
} catch {
  Write-Output ("Failed to set processor minimum performance: " + $_.Exception.Message)
}

try {
  powercfg -setacvalueindex scheme_current sub_processor PROCTHROTTLEMIN 94
  powercfg -setactive scheme_current
  Write-Output 'Set minimum processor performance on AC to 94%.'
} catch {
  Write-Output ("Failed to set processor minimum performance: " + $_.Exception.Message)
}

try {
  powercfg -setacvalueindex scheme_current sub_processor PROCTHROTTLEMIN 95
  powercfg -setactive scheme_current
  Write-Output 'Set minimum processor performance on AC to 95%.'
} catch {
  Write-Output ("Failed to set processor minimum performance: " + $_.Exception.Message)
}

$path = 'HKLM:\SYSTEM\CurrentControlSet\Control\DeviceGuard\Scenarios\HypervisorEnforcedCodeIntegrity'
try {
  New-Item -Path $path -Force | Out-Null
  New-ItemProperty -Path $path -Name 'Enabled' -Value 0 -PropertyType DWord -Force | Out-Null
  Write-Output 'Disabled Hypervisor-enforced Code Integrity (Core Isolation). Reboot required.'
} catch {
  Write-Output ("Failed to disable Core Isolation: " + $_.Exception.Message)
}

$path = 'HKLM:\SYSTEM\CurrentControlSet\Control\DeviceGuard'
try {
  New-Item -Path $path -Force | Out-Null
  New-ItemProperty -Path $path -Name 'EnableVirtualizationBasedSecurity' -Value 0 -PropertyType DWord -Force | Out-Null
  Write-Output 'Disabled Virtualization Based Security. Reboot required.'
} catch {
  Write-Output ("Failed to disable VBS: " + $_.Exception.Message)
}

try {
  $dev = Get-PnpDevice -FriendlyName '*High precision event timer*' -ErrorAction SilentlyContinue
  if ($dev) {
    Disable-PnpDevice -InstanceId $dev.InstanceId -Confirm:$false -ErrorAction SilentlyContinue
    Write-Output 'Disabled High Precision Event Timer device.'
  } else {
    Write-Output 'HPET device not found.'
  }
} catch {
  Write-Output ("Failed to adjust HPET device: " + $_.Exception.Message)
}

try {
  bcdedit /set disabledynamictick yes
  Write-Output 'Set disabledynamictick = yes (reboot required).'
} catch {
  Write-Output ("Failed to set disabledynamictick: " + $_.Exception.Message)
}

try {
  New-Item -Path 'HKCU:\Software\Microsoft\Windows\CurrentVersion\Themes\Personalize' -Force | Out-Null
  New-ItemProperty -Path 'HKCU:\Software\Microsoft\Windows\CurrentVersion\Themes\Personalize' -Name 'EnableTransparency' -Value 0 -PropertyType DWord -Force | Out-Null
  Write-Output 'Disabled transparency effects.'
} catch {
  Write-Output ("Failed to disable transparency: " + $_.Exception.Message)
}

$path = 'HKCU:\Control Panel\Desktop\WindowMetrics'
try {
  New-Item -Path $path -Force | Out-Null
  New-ItemProperty -Path $path -Name 'MinAnimate' -Value 0 -PropertyType String -Force | Out-Null
  Write-Output 'Disabled minimize/maximize animations.'
} catch {
  Write-Output ("Failed to adjust animation settings: " + $_.Exception.Message)
}

try {
  New-Item -Path 'HKCU:\Software\Microsoft\Windows\CurrentVersion\Explorer\Advanced' -Force | Out-Null
  New-ItemProperty -Path 'HKCU:\Software\Microsoft\Windows\CurrentVersion\Explorer\Advanced' -Name 'Start_TrackDocs' -Value 0 -PropertyType DWord -Force | Out-Null
  Write-Output 'Disabled Start menu recent/recommendation tracking.'
} catch {
  Write-Output ("Failed to adjust Start menu tracking: " + $_.Exception.Message)
}

try {
  New-Item -Path 'HKCU:\Software\Microsoft\Windows\CurrentVersion\Explorer\Advanced' -Force | Out-Null
  New-ItemProperty -Path 'HKCU:\Software\Microsoft\Windows\CurrentVersion\Explorer\Advanced' -Name 'ShowStatusBar' -Value 0 -PropertyType DWord -Force | Out-Null
  New-ItemProperty -Path 'HKCU:\Software\Microsoft\Windows\CurrentVersion\Explorer\Advanced' -Name 'NavPaneExpandToCurrentFolder' -Value 0 -PropertyType DWord -Force | Out-Null
  Write-Output 'Applied hardcore Explorer simplification tweaks.'
} catch {
  Write-Output ("Failed to tweak Explorer: " + $_.Exception.Message)
}

try {
  New-Item -Path 'HKCU:\Software\Microsoft\Windows\CurrentVersion\BackgroundAccessApplications' -Force | Out-Null
  New-ItemProperty -Path 'HKCU:\Software\Microsoft\Windows\CurrentVersion\BackgroundAccessApplications' -Name 'GlobalUserDisabled' -Value 1 -PropertyType DWord -Force | Out-Null
  Write-Output 'Disabled UWP background apps globally.'
} catch {
  Write-Output ("Failed to disable background apps: " + $_.Exception.Message)
}

try {
  $runKey = 'HKCU:\Software\Microsoft\Windows\CurrentVersion\Run'
  Remove-ItemProperty -Path $runKey -Name 'com.squirrel.Teams.Teams' -ErrorAction SilentlyContinue
  Remove-ItemProperty -Path $runKey -Name 'com.squirrel.Teams.TeamsUpdater' -ErrorAction SilentlyContinue
} catch {}
try {
  Get-Process Teams*,ms-teams* -ErrorAction SilentlyContinue | Stop-Process -Force -ErrorAction SilentlyContinue
} catch {}
Write-Output 'Attempted to disable and stop Microsoft Teams.'

try {
  New-Item -Path 'HKCU:\Software\Microsoft\Windows\CurrentVersion\ContentDeliveryManager' -Force | Out-Null
  New-ItemProperty -Path 'HKCU:\Software\Microsoft\Windows\CurrentVersion\ContentDeliveryManager' -Name 'RotatingLockScreenEnabled' -Value 0 -PropertyType DWord -Force | Out-Null
  New-ItemProperty -Path 'HKCU:\Software\Microsoft\Windows\CurrentVersion\ContentDeliveryManager' -Name 'RotatingLockScreenOverlayEnabled' -Value 0 -PropertyType DWord -Force | Out-Null
  Write-Output 'Disabled Windows Spotlight lock screen.'
} catch {
  Write-Output ("Failed to disable Spotlight: " + $_.Exception.Message)
}

try {
  New-Item -Path 'HKCU:\Software\Microsoft\Windows\CurrentVersion\ContentDeliveryManager' -Force | Out-Null
