Write-Output "=== FALCON INPUT LAB: HID POWER SCAN ==="
Write-Output ""

# Show USB selective suspend high-level hint (power plan)
try {
  Write-Output "USB Selective Suspend (Power Plan) quick check:"
  $usbGuid = "2a737441-1930-4402-8d77-b2bebba308a3"
  $subUsb = "SUB_USB"
  $scheme = "SCHEME_CURRENT"
  $query = powercfg /QUERY $scheme $subUsb $usbGuid 2>$null
  if ($query) {
    Write-Output $query
  } else {
    Write-Output "  (Could not query USB selective suspend via powercfg.)"
  }
