Write-Output ""
Write-Output "Scanning HID/USB devices registry for EnhancedPowerManagementEnabled / DeviceIdleEnabled..."
Write-Output ""

$bases = @(
  "HKLM:\SYSTEM\CurrentControlSet\Enum\USB",
  "HKLM:\SYSTEM\CurrentControlSet\Enum\HID"
)

$found = 0
foreach ($base in $bases) {
  if (-not (Test-Path $base)) { continue }
  Get-ChildItem $base -Recurse -ErrorAction SilentlyContinue | ForEach-Object {
    $devParams = Join-Path $_.PsPath "Device Parameters"
    if (Test-Path $devParams) {
      try {
        $p = Get-ItemProperty $devParams -ErrorAction SilentlyContinue
      } catch {
        return
      }
      $hasEpm = $p.PSObject.Properties.Name -contains "EnhancedPowerManagementEnabled"
      $hasIdle = $p.PSObject.Properties.Name -contains "DeviceIdleEnabled"
      if ($hasEpm -or $hasIdle) {
        $found++
        Write-Output ("Device Parameters: {0}" -f $devParams)
        if ($hasEpm) {
          Write-Output ("  EnhancedPowerManagementEnabled: {0}" -f $p.EnhancedPowerManagementEnabled)
        }
        if ($hasIdle) {
          Write-Output ("  DeviceIdleEnabled: {0}" -f $p.DeviceIdleEnabled)
        }
        Write-Output ""
      }
    }
  }
}

if ($found -eq 0) {
  Write-Output "No HID/USB device parameters with EnhancedPowerManagementEnabled / DeviceIdleEnabled were found."
} else {
  Write-Output ("Total devices with HID power parameters: {0}" -f $found)
}

Write-Output ""
Write-Output "Tip: combine this with 'Peripherals' toggles (USB selective suspend off + HID idle timeout off) for max stability."
Write-Output ""
Write-Output "=== END OF HID POWER SCAN ==="

Write-Output "=== FALCON INPUT LAB: DISABLE HID ENHANCED POWER MANAGEMENT ==="
Write-Output ""
Write-Output "This will try to set EnhancedPowerManagementEnabled = 0 and DeviceIdleEnabled = 0 for HID/USB devices that expose these flags."
Write-Output "Admin rights required. Some devices may ignore this setting."
Write-Output ""

$bases = @(
  "HKLM:\SYSTEM\CurrentControlSet\Enum\USB",
  "HKLM:\SYSTEM\CurrentControlSet\Enum\HID"
)

$changed = 0
foreach ($base in $bases) {
  if (-not (Test-Path $base)) { continue }
  Get-ChildItem $base -Recurse -ErrorAction SilentlyContinue | ForEach-Object {
    $devParams = Join-Path $_.PsPath "Device Parameters"
    if (Test-Path $devParams) {
      try {
        $p = Get-ItemProperty $devParams -ErrorAction SilentlyContinue
      } catch {
        return
      }

      $didSomething = $false

      if ($p.PSObject.Properties.Name -contains "EnhancedPowerManagementEnabled") {
        if ($p.EnhancedPowerManagementEnabled -ne 0) {
          try {
            Set-ItemProperty -Path $devParams -Name "EnhancedPowerManagementEnabled" -Value 0 -Type DWord -ErrorAction SilentlyContinue
            Write-Output ("  Set EnhancedPowerManagementEnabled=0 at {0}" -f $devParams)
            $didSomething = $true
          } catch {
            Write-Output ("  Failed to set EnhancedPowerManagementEnabled at {0}: {1}" -f $devParams, $_.Exception.Message)
          }
        }
      }

      if ($p.PSObject.Properties.Name -contains "DeviceIdleEnabled") {
        if ($p.DeviceIdleEnabled -ne 0) {
          try {
            Set-ItemProperty -Path $devParams -Name "DeviceIdleEnabled" -Value 0 -Type DWord -ErrorAction SilentlyContinue
            Write-Output ("  Set DeviceIdleEnabled=0 at {0}" -f $devParams)
            $didSomething = $true
          } catch {
            Write-Output ("  Failed to set DeviceIdleEnabled at {0}: {1}" -f $devParams, $_.Exception.Message)
          }
        }
      }

      if ($didSomething) {
        $GLOBALS:changed++
      }
    }
  }
}

if ($changed -gt 0) {
  Write-Output ""
  Write-Output ("Total device parameter keys adjusted: {0}" -f $changed)
} else {
  Write-Output ""
  Write-Output "No EnhancedPowerManagementEnabled / DeviceIdleEnabled values needed changing."
}

