Write-Output "=== FALCON MEMORY LAB: RAM STABILITY & CONFIG SCAN ==="
Write-Output ""

# Basic physical memory layout
try {
  $sticks = Get-CimInstance Win32_PhysicalMemory -ErrorAction SilentlyContinue
  if ($sticks) {
    Write-Output ("Detected RAM modules: {0}" -f $sticks.Count)
    $i = 0
    foreach ($m in $sticks) {
      $i++
      $sizeGB = [math]::Round(($m.Capacity / 1GB), 2)
      $speed  = $m.Speed
      $cfg    = $m.ConfiguredClockSpeed
      $manu   = $m.Manufacturer
      $part   = $m.PartNumber
      $bank   = $m.BankLabel
      Write-Output ("  Stick #{0}: {1} GB @ {2} MHz (Configured: {3} MHz) | {4} {5} | Bank: {6}" -f $i, $sizeGB, $speed, $cfg, $manu, $part, $bank)
    }

    # Heuristic mismatch checks
    $sizes = $sticks | ForEach-Object { [math]::Round(($_.Capacity / 1GB), 2) }
    $speeds = $sticks | ForEach-Object { $_.Speed }
    if (($sizes | Select-Object -Unique).Count -gt 1) {
      Write-Output ""
      Write-Output "Warning: Detected mixed RAM sizes. Dual-channel symmetry and stability may be reduced."
    }
    if (($speeds | Select-Object -Unique).Count -gt 1) {
      Write-Output ""
      Write-Output "Warning: Detected mixed RAM speeds. All sticks will run at the slowest speed; stability can vary."
    }
  } else {
    Write-Output "No physical memory modules reported by Win32_PhysicalMemory."
  }
} catch {
  Write-Output "Failed to query Win32_PhysicalMemory."
}

Write-Output ""

# Approximate XMP / frequency sanity hints
try {
  if ($sticks -and $sticks.Count -gt 0) {
    $avgCfg = ($sticks | Where-Object { $_.ConfiguredClockSpeed -gt 0 } | Measure-Object ConfiguredClockSpeed -Average).Average
    if ($avgCfg) {
      $avgCfg = [math]::Round($avgCfg,0)
      Write-Output ("Average ConfiguredClockSpeed: {0} MHz" -f $avgCfg)
      if ($avgCfg -lt 2600) {
        Write-Output "Hint: Configured clock looks like JEDEC/base profile. If you have an XMP/DOCP/EXPO kit, enable it in BIOS."
      }
    } else {
      Write-Output "ConfiguredClockSpeed not exposed; cannot approximate XMP state."
    }
  }
} catch {
  Write-Output "Failed to analyze ConfiguredClockSpeed."
}

Write-Output ""

# OS memory state
try {
  $os = Get-CimInstance Win32_OperatingSystem -ErrorAction SilentlyContinue
  if ($os) {
    $total = [double]$os.TotalVisibleMemorySize
    $free  = [double]$os.FreePhysicalMemory
    $usedMB = ($total - $free) / 1024.0
    $totalMB = $total / 1024.0
    $usedPct = ($usedMB / $totalMB) * 100.0
    Write-Output ("OS Memory: Used {0:N1} / {1:N1} MB ({2:N1} %)" -f $usedMB, $totalMB, $usedPct)
  }
} catch {
  Write-Output "Failed to query OS memory usage."
}

