Write-Output "=== FALCON MEMORY LAB: LOW-LATENCY MEMORY MODE (AGGRESSIVE) ==="
Write-Output ""
Write-Output "Applying registry tweaks aimed at keeping more code in RAM and avoiding heavy paging behavior."
Write-Output "This is best for systems with plenty of RAM and SSDs. It can increase RAM usage."
Write-Output ""

$mmKey = "HKLM:\SYSTEM\CurrentControlSet\Control\Session Manager\Memory Management"

try {
  if (-not (Test-Path $mmKey)) {
    Write-Output "Memory Management key not found; aborting."
    return
  }

  # DisablePagingExecutive = 1 (keep kernel & drivers in memory)
  try {
    New-ItemProperty -Path $mmKey -Name "DisablePagingExecutive" -PropertyType DWord -Value 1 -Force | Out-Null
    Write-Output "Set DisablePagingExecutive = 1"
  } catch {
    Write-Output ("Failed to set DisablePagingExecutive: {0}" -f $_.Exception.Message)
  }

  # LargeSystemCache = 0 (prefer programs vs huge file cache for gaming desktops)
  try {
    New-ItemProperty -Path $mmKey -Name "LargeSystemCache" -PropertyType DWord -Value 0 -Force | Out-Null
    Write-Output "Set LargeSystemCache = 0 (programs prioritized)."
  } catch {
    Write-Output ("Failed to set LargeSystemCache: {0}" -f $_.Exception.Message)
  }

  # ClearPageFileAtShutdown = 0 (avoid long shutdowns)
  try {
    New-ItemProperty -Path $mmKey -Name "ClearPageFileAtShutdown" -PropertyType DWord -Value 0 -Force | Out-Null
    Write-Output "Set ClearPageFileAtShutdown = 0 (no full pagefile wipe on shutdown)."
  } catch {
    Write-Output ("Failed to set ClearPageFileAtShutdown: {0}" -f $_.Exception.Message)
  }

} catch {
  Write-Output ("Error while writing Memory Management registry values: {0}" -f $_.Exception.Message)
}

Write-Output ""
Write-Output "A reboot is recommended for all memory management changes to fully apply."
Write-Output "=== END OF LOW-LATENCY MEMORY MODE APPLY ==="

