Write-Output "=== FALCON SAFEGUARD: CREATE SNAPSHOT ==="
Write-Output ""
Write-Output "This will snapshot core service startup types and selected low-level settings so you can restore them later."
Write-Output "Run this when your system is in a known-good state before applying extreme tweaks."
Write-Output ""

$logsDir = Join-Path $PSScriptRoot "..\logs"
try {
  if (-not (Test-Path $logsDir)) {
    New-Item -ItemType Directory -Path $logsDir -Force | Out-Null
  }
} catch {
  Write-Output "Failed to create logs directory."
}

$timestamp = Get-Date -Format "yyyyMMdd_HHmmss"
$snapFile = Join-Path $logsDir ("falcon_safeguard_snapshot_{0}.json" -f $timestamp)

$snapshot = [ordered]@{}
$snapshot.TimestampUtc = (Get-Date).ToUniversalTime().ToString("o")

# --- Services snapshot ---
Write-Output "Snapshotting service startup types..."
$svcList = @()
try {
  $wmiServices = Get-WmiObject Win32_Service -ErrorAction SilentlyContinue
  foreach ($s in $wmiServices) {
    # StartMode values like 'Auto', 'Manual', 'Disabled'
    $svcList += [ordered]@{
      Name      = $s.Name
      StartMode = $s.StartMode
    }
  }
  $snapshot.Services = $svcList
  Write-Output ("Captured {0} services." -f $svcList.Count)
} catch {
  Write-Output ("Failed to snapshot services: {0}" -f $_.Exception.Message)
  $snapshot.Services = @()
}

# --- Memory Management key (selected values) ---
$mmKey = "HKLM:\SYSTEM\CurrentControlSet\Control\Session Manager\Memory Management"
$mmSnap = @{}
$mmNames = @("DisablePagingExecutive","LargeSystemCache","ClearPageFileAtShutdown")
try {
  if (Test-Path $mmKey) {
    $mmProps = Get-ItemProperty -Path $mmKey -ErrorAction SilentlyContinue
    foreach ($n in $mmNames) {
      if ($mmProps.PSObject.Properties.Name -contains $n) {
        $mmSnap[$n] = $mmProps.$n
      }
    }
  }
} catch {}
$snapshot.MemoryManagement = $mmSnap

# --- PowerThrottling key (PowerThrottlingOff) ---
$ptKey = "HKLM:\SYSTEM\CurrentControlSet\Control\Power\PowerThrottling"
$ptSnap = @{}
try {
  if (Test-Path $ptKey) {
    $ptProps = Get-ItemProperty -Path $ptKey -ErrorAction SilentlyContinue
    if ($ptProps.PSObject.Properties.Name -contains "PowerThrottlingOff") {
      $ptSnap["PowerThrottlingOff"] = $ptProps.PowerThrottlingOff
    }
  }
} catch {}
$snapshot.PowerThrottling = $ptSnap

# --- MMAgent state (memory compression / page combining) ---
$mmAgentSnap = @{}
try {
  if (Get-Command -Name Get-MMAgent -ErrorAction SilentlyContinue) {
    $mma = Get-MMAgent
    $mmAgentSnap["MemoryCompressionEnabled"] = $mma.MemoryCompression
    $mmAgentSnap["PageCombiningEnabled"]     = $mma.PageCombining
  }
} catch {}
$snapshot.MMAgent = $mmAgentSnap

# Write snapshot json
try {
  $json = $snapshot | ConvertTo-Json -Depth 6
  $json | Out-File -FilePath $snapFile -Encoding UTF8 -Force
  Write-Output ("Safeguard snapshot saved to: {0}" -f $snapFile)
} catch {
  Write-Output ("Failed to write snapshot file: {0}" -f $_.Exception.Message)
}

Write-Output ""
Write-Output "Keep this snapshot if you plan to go very extreme with Process Lab / Debloat / Memory tweaks."
Write-Output "=== END OF SAFEGUARD SNAPSHOT ==="

Write-Output "=== FALCON SAFEGUARD: RESTORE FROM LATEST SNAPSHOT ==="
Write-Output ""

$logsDir = Join-Path $PSScriptRoot "..\logs"
if (-not (Test-Path $logsDir)) {
  Write-Output "No logs directory found. You must create a Safeguard snapshot first."
  return
}

$snapFiles = Get-ChildItem $logsDir -Filter "falcon_safeguard_snapshot_*.json" -ErrorAction SilentlyContinue | Sort-Object LastWriteTime -Descending
if (-not $snapFiles -or $snapFiles.Count -eq 0) {
  Write-Output "No Safeguard snapshot files found. Run 'Create Safeguard Snapshot' first."
  return
}

$snapFile = $snapFiles[0].FullName
Write-Output ("Using snapshot: {0}" -f $snapFile)

try {
  $snapJson = Get-Content $snapFile -Raw -ErrorAction SilentlyContinue
  $snap = $snapJson | ConvertFrom-Json -ErrorAction SilentlyContinue
} catch {
  Write-Output "Failed to parse snapshot JSON."
  return
}

# --- Restore services startup types ---
$restoredSvc = 0
try {
  if ($snap.Services) {
    foreach ($s in $snap.Services) {
      $name = $s.Name
      $mode = $s.StartMode
      if ([string]::IsNullOrWhiteSpace($name) -or [string]::IsNullOrWhiteSpace($mode)) { continue }

      # Map WMI StartMode ('Auto','Manual','Disabled') to Set-Service StartupType
      $startupType = $null
      switch -Regex ($mode) {
        "Auto"     { $startupType = "Automatic" }
        "Manual"   { $startupType = "Manual" }
        "Disabled" { $startupType = "Disabled" }
      }
      if (-not $startupType) { continue }

      try {
        Set-Service -Name $name -StartupType $startupType -ErrorAction SilentlyContinue
        $restoredSvc++
      } catch {
        # ignore individual failures
      }
    }
  }
  Write-Output ("Restored startup type for ~{0} services (best effort)." -f $restoredSvc)
} catch {
  Write-Output ("Error while restoring services: {0}" -f $_.Exception.Message)
}

# --- Restore Memory Management values ---
$mmKey = "HKLM:\SYSTEM\CurrentControlSet\Control\Session Manager\Memory Management"
try {
  if ($snap.MemoryManagement) {
    if (-not (Test-Path $mmKey)) {
      New-Item -Path $mmKey -Force | Out-Null
    }
    foreach ($k in $snap.MemoryManagement.Keys) {
      $val = $snap.MemoryManagement[$k]
      try {
        New-ItemProperty -Path $mmKey -Name $k -PropertyType DWord -Value $val -Force | Out-Null
        Write-Output ("Restored Memory Management value {0}={1}" -f $k, $val)
      } catch {
        Write-Output ("Failed to restore Memory Management value {0}: {1}" -f $k, $_.Exception.Message)
      }
    }
  }
} catch {
  Write-Output ("Error while restoring Memory Management values: {0}" -f $_.Exception.Message)
}

# --- Restore PowerThrottlingOff ---
$ptKey = "HKLM:\SYSTEM\CurrentControlSet\Control\Power\PowerThrottling"
try {
  if ($snap.PowerThrottling) {
    if (-not (Test-Path $ptKey)) {
      New-Item -Path $ptKey -Force | Out-Null
    }
    if ($snap.PowerThrottling.PSObject.Properties.Name -contains "PowerThrottlingOff") {
      $val = $snap.PowerThrottling.PowerThrottlingOff
      if ($null -ne $val) {
        try {
          New-ItemProperty -Path $ptKey -Name "PowerThrottlingOff" -PropertyType DWord -Value $val -Force | Out-Null
          Write-Output ("Restored PowerThrottlingOff={0}" -f $val)
        } catch {
          Write-Output ("Failed to restore PowerThrottlingOff: {0}" -f $_.Exception.Message)
        }
      }
    }
  }
} catch {
  Write-Output ("Error while restoring PowerThrottling settings: {0}" -f $_.Exception.Message)
}

# --- Restore MMAgent flags for MemoryCompression & PageCombining ---
try {
  if ($snap.MMAgent -and (Get-Command -Name Get-MMAgent -ErrorAction SilentlyContinue)) {
    $mc = $null
    $pc = $null
    if ($snap.MMAgent.PSObject.Properties.Name -contains "MemoryCompressionEnabled") {
      $mc = [bool]$snap.MMAgent.MemoryCompressionEnabled
    }
    if ($snap.MMAgent.PSObject.Properties.Name -contains "PageCombiningEnabled") {
      $pc = [bool]$snap.MMAgent.PageCombiningEnabled
    }

    if ($null -ne $mc) {
      try {
        if ($mc) { Enable-MMAgent -MemoryCompression -ErrorAction SilentlyContinue }
        else     { Disable-MMAgent -MemoryCompression -ErrorAction SilentlyContinue }
        Write-Output ("Restored MemoryCompressionEnabled to {0}" -f $mc)
      } catch {
        Write-Output ("Failed to restore MemoryCompressionEnabled: {0}" -f $_.Exception.Message)
      }
    }

    if ($null -ne $pc) {
      try {
        if ($pc) { Enable-MMAgent -PageCombining -ErrorAction SilentlyContinue }
        else     { Disable-MMAgent -PageCombining -ErrorAction SilentlyContinue }
        Write-Output ("Restored PageCombiningEnabled to {0}" -f $pc)
      } catch {
        Write-Output ("Failed to restore PageCombiningEnabled: {0}" -f $_.Exception.Message)
      }
    }
  }
} catch {
  Write-Output ("Error while restoring MMAgent flags: {0}" -f $_.Exception.Message)
}

Write-Output ""
Write-Output "Safeguard restore complete (best effort). A reboot is recommended to fully apply service and memory changes."
Write-Output "=== END OF SAFEGUARD RESTORE ==="

Write-Output "=== FALCON AUTO-RECOMMEND ENGINE ==="
Write-Output ""

# Helper: safe int parse
function To-Int($val, $default=0) {
  try {
    return [int]$val
  } catch {
    return $default
  }
}

# OS
$osCaption = ""
$osVer = ""
try {
  $os = Get-CimInstance Win32_OperatingSystem
  $osCaption = $os.Caption
  $osVer = $os.Version
  Write-Output ("OS: {0} (Version {1})" -f $osCaption, $osVer)
} catch {
  Write-Output "OS: <failed to query>"
}

# CPU
$cpuName = ""
$cpuMan = ""
$cpuCores = 0
$cpuThreads = 0
try {
  $cpu = Get-CimInstance Win32_Processor
  $cpuName = $cpu.Name
  $cpuMan = $cpu.Manufacturer
  $cpuCores = To-Int $cpu.NumberOfCores
  $cpuThreads = To-Int $cpu.NumberOfLogicalProcessors
  Write-Output ""
  Write-Output "CPU:"
  Write-Output ("  {0} | Cores: {1} | Threads: {2}" -f $cpuName, $cpuCores, $cpuThreads)
} catch {
  Write-Output "CPU: <failed to query>"
}

# GPU (primary)
$gpuName = ""
$gpuVendor = ""
$gpuVramGB = 0
try {
  $gpus = Get-CimInstance Win32_VideoController | Where-Object { $_.AdapterRAM -gt 0 }
  if ($gpus) {
    # pick the one with max VRAM
    $primary = $gpus | Sort-Object AdapterRAM -Descending | Select-Object -First 1
    $gpuName = $primary.Name
    $gpuVramGB = [math]::Round($primary.AdapterRAM / 1GB, 2)
    if ($gpuName -match "NVIDIA") { $gpuVendor = "NVIDIA" }
    elseif ($gpuName -match "AMD" -or $gpuName -match "Radeon") { $gpuVendor = "AMD" }
    elseif ($gpuName -match "Intel") { $gpuVendor = "Intel" }
    else { $gpuVendor = "Unknown" }
    Write-Output ""
    Write-Output ("GPU: {0} ({1} GB, Vendor: {2})" -f $gpuName, $gpuVramGB, $gpuVendor)
  } else {
    Write-Output "GPU: <no VRAM GPUs found>"
  }
} catch {
  Write-Output "GPU: <failed to query>"
}

# RAM total
$totalRamGB = 0
try {
  $mem = Get-CimInstance Win32_PhysicalMemory
  $totalBytes = ($mem | Measure-Object -Property Capacity -Sum).Sum
  if ($totalBytes -gt 0) {
    $totalRamGB = [math]::Round($totalBytes / 1GB, 2)
  }
  Write-Output ""
  Write-Output ("RAM: {0} GB total" -f $totalRamGB)
} catch {
  Write-Output "RAM: <failed to query>"
}

# Form-factor (desktop vs laptop)
$form = "Unknown"
try {
  $cs = Get-CimInstance Win32_ComputerSystem
  $ptype = To-Int $cs.PCSystemType
  switch ($ptype) {
    1 { $form = "Desktop" }
    2 { $form = "Laptop" }
    3 { $form = "Workstation" }
    default { $form = "Unknown" }
  }
  Write-Output ""
  Write-Output ("Form Factor: {0}" -f $form)
} catch {
  Write-Output "Form Factor: <failed to query>"
}

# Power plan
$activePlanRaw = ""
try {
  $activePlanRaw = (powercfg /getactivescheme 2>$null)
  Write-Output ""
  Write-Output ("Power Plan: {0}" -f $activePlanRaw.Trim())
} catch {
  Write-Output "Power Plan: <failed to query>"
}

# HAGS
$hags = "Unknown"
try {
  $gd = Get-ItemProperty -Path 'HKLM:\SYSTEM\CurrentControlSet\Control\GraphicsDrivers' -ErrorAction SilentlyContinue
  if ($gd -and $gd.PSObject.Properties.Name -contains "HwSchMode") {
    switch ([int]$gd.HwSchMode) {
      1 { $hags = "Off" }
      2 { $hags = "On" }
      default { $hags = "Default (driver controlled)" }
    }
  }
  Write-Output ""
  Write-Output ("HAGS (HwSchMode): {0}" -f $hags)
} catch {
  Write-Output "HAGS: <failed to query>"
}

# Device Guard / VBS
$vbs = "Unknown"
try {
  $dg = Get-CimInstance Win32_DeviceGuard -ErrorAction SilentlyContinue
  if ($dg) {
    $running = $dg.SecurityServicesRunning
    if ($running -contains 1 -or $running -contains 2) {
      $vbs = "On"
    } else {
      $vbs = "Off"
    }
  }
  Write-Output ""
  Write-Output ("VBS/HVCI: {0}" -f $vbs)
} catch {
  Write-Output "VBS/HVCI: <failed to query>"
}

# Defender realtime
$defState = "Unknown"
try {
  $mp = Get-MpComputerStatus -ErrorAction SilentlyContinue
  if ($mp) {
    $defState = ($mp.RealTimeProtectionEnabled) ? "On" : "Off"
  }
  Write-Output ""
  Write-Output ("Defender realtime: {0}" -f $defState)
} catch {
  Write-Output "Defender: <failed to query>"
}

# Basic Falcon profile classification
Write-Output ""
Write-Output "=== Falcon Classification ==="

$profile = "GENERIC"
if ($form -eq "Desktop") {
  if ($totalRamGB -ge 32 -and $gpuVramGB -ge 8 -and $cpuThreads -ge 16) {
    $profile = "DESKTOP-HIGH-END"
  } elseif ($totalRamGB -ge 16 -and $gpuVramGB -ge 6) {
    $profile = "DESKTOP-MID"
  } else {
    $profile = "DESKTOP-LOW"
  }
} elseif ($form -eq "Laptop") {
  if ($totalRamGB -ge 16 -and $gpuVramGB -ge 6) {
    $profile = "LAPTOP-MID"
  } else {
    $profile = "LAPTOP-LOW"
  }
}

Write-Output ("Profile: {0}" -f $profile)
Write-Output ""

# Recommend Falcon preset text
Write-Output "=== Recommended Falcon Mode ==="

switch ($profile) {
  "DESKTOP-HIGH-END" {
    Write-Output "Recommended: Falcon Extreme Desktop preset + Hardcore Tweaks (with caution)."
    Write-Output " - Enable Game Mode, Speed Core, full Process Lab modes."
    Write-Output " - Hardcore allowed, but keep Windows Update/Defender restore preset available."
  }
  "DESKTOP-MID" {
    Write-Output "Recommended: Falcon Competitive preset."
    Write-Output " - Game Mode + Speed Core + main debloat, but only partial Hardcore."
    Write-Output " - Avoid killing ALL update/telemetry services; keep Defender mostly on."
  }
  "DESKTOP-LOW" {
    Write-Output "Recommended: Falcon Stability + FPS preset."
    Write-Output " - Focus on debloat, core scheduler, and Process Lab; avoid most Hardcore until stable."
  }
  "LAPTOP-MID" {
    Write-Output "Recommended: Falcon Laptop Competitive preset."
    Write-Output " - Favor power plan tuning + debloat + network tweaks, but keep thermal and battery safety."
  }
  "LAPTOP-LOW" {
    Write-Output "Recommended: Falcon Laptop Safe preset."
    Write-Output " - Use light debloat and network tweaks, skip aggressive kernel/mitigation changes."
  }
  default {
    Write-Output "Recommended: Start with Balanced Falcon preset, then selectively add Hardcore tweaks."
  }
}

# Extra targeted hints
Write-Output ""
Write-Output "=== Extra Hints ==="

if ($totalRamGB -lt 16) {
  Write-Output "- RAM is under 16 GB: prioritize debloat and Process Lab over heavy Hardcore tweaks."
}

if ($gpuVramGB -lt 6 -and $gpuVramGB -gt 0) {
  Write-Output "- GPU VRAM < 6 GB: use conservative texture settings and skip extreme resolution scales."
}

if ($form -eq "Laptop") {
  Write-Output "- Laptop detected: be careful with disabling power management and battery/thermal controls."
}

if ($hags -eq "On" -and $gpuVendor -eq "NVIDIA") {
  Write-Output "- HAGS is ON: good for some modern titles, but if you see stutter, test Falcon's HAGS-OFF tweak."
} elseif ($hags -eq "Off" -and $gpuVendor -eq "NVIDIA") {
  Write-Output "- HAGS is OFF: you can test enabling it for DX12 titles using Falcon's HAGS-ON tweak."
}

if ($vbs -eq "On") {
  Write-Output "- VBS/HVCI is ON: better security but some latency overhead. Only disable if you understand the risk."
}

if ($defState -eq "Off") {
  Write-Output "- Defender realtime appears OFF: security is reduced. Make sure this is intentional before going full Hardcore."
}

Write-Output ""
Write-Output "=== End of Recommendation ==="

Write-Output "=== FALCON THERMAL ENGINE: DISABLE GLOBAL POWER THROTTLING ==="
Write-Output ""
Write-Output "This will set PowerThrottlingOff=1 under the global PowerThrottling key."
Write-Output "It tells Windows not to apply modern background power-throttling heuristics."
Write-Output "Use for plugged-in gaming desktops; laptops may lose battery life."
Write-Output ""

$ptKey = "HKLM:\SYSTEM\CurrentControlSet\Control\Power\PowerThrottling"

try {
  if (-not (Test-Path $ptKey)) {
    New-Item -Path $ptKey -Force | Out-Null
  }
  New-ItemProperty -Path $ptKey -Name "PowerThrottlingOff" -Value 1 -PropertyType DWord -Force | Out-Null
  Write-Output "Set PowerThrottlingOff = 1 (global)."
} catch {
  Write-Output ("Failed to set PowerThrottlingOff: {0}" -f $_.Exception.Message)
}

Write-Output ""
Write-Output "Note: Some power features (especially on laptops) rely on throttling for thermals/battery."
Write-Output "      Only use this on systems where cooling and power are sufficient."
Write-Output "=== END OF GLOBAL POWER THROTTLING DISABLE ==="

$paths = @(
  "$env:ProgramFiles\FanControl\FanControl.exe",
  "$env:ProgramFiles (x86)\FanControl\FanControl.exe",
  "$env:LOCALAPPDATA\FanControl\FanControl.exe",
  "$env:USERPROFILE\Desktop\FanControl\FanControl.exe",
  "$env:USERPROFILE\Downloads\FanControl\FanControl.exe"
)
$exe = $paths | Where-Object { Test-Path $_ } | Select-Object -First 1
if ($exe) {
  Start-Process -FilePath $exe | Out-Null
  Write-Output "Launched: $exe"
} else {
  Write-Output "FanControl.exe not found in common paths."
  Write-Output "Tip: install to C:\Program Files\FanControl\FanControl.exe (or %LOCALAPPDATA%\FanControl\FanControl.exe)."
}
