try {
  $ifaces = Get-NetIPInterface -AddressFamily IPv4 -ErrorAction SilentlyContinue
  foreach ($iface in $ifaces) {
    $id = $iface.InterfaceIndex
    $reg = "HKLM:\SYSTEM\CurrentControlSet\Services\Tcpip\Parameters\Interfaces\{0}" -f $iface.InterfaceGuid
    New-Item -Path $reg -Force | Out-Null
    New-ItemProperty -Path $reg -Name 'TcpNoDelay' -Value 1 -PropertyType DWord -Force | Out-Null
