Write-Output "=== FALCON NETWORK LAB: PING & JITTER TEST ==="
Write-Output ""
Write-Output "Testing latency to common targets (50 pings each)..."
Write-Output ""

$targets = @(
  "8.8.8.8",       # Google DNS
  "1.1.1.1",       # Cloudflare DNS
  "google.com"     # General web
)

foreach ($t in $targets) {
  Write-Output ("--- Target: {0} ---" -f $t)
  try {
    $res = Test-Connection -ComputerName $t -Count 50 -ErrorAction SilentlyContinue
    if ($res) {
      $avg = ($res | Measure-Object -Property ResponseTime -Average).Average
      $min = ($res | Measure-Object -Property ResponseTime -Minimum).Minimum
      $max = ($res | Measure-Object -Property ResponseTime -Maximum).Maximum
      $stdev = [math]::Sqrt((($res | ForEach-Object { ($_.ResponseTime - $avg) * ($_.ResponseTime - $avg) }) | Measure-Object -Sum).Sum / ($res.Count))
      Write-Output ("  Avg:  {0:N2} ms" -f $avg)
      Write-Output ("  Min:  {0:N2} ms" -f $min)
      Write-Output ("  Max:  {0:N2} ms" -f $max)
      Write-Output ("  Jitter (std dev): {0:N2} ms" -f $stdev)
    } else {
      Write-Output "  No ping response."
    }
  } catch {
    Write-Output ("  Failed to ping: {0}" -f $_.Exception.Message)
  }
  Write-Output ""
}

Write-Output "Tip: High jitter (std dev) or frequent timeouts == unstable route or Wi-Fi issues."
Write-Output "=== END OF NETWORK PING/JITTER TEST ==="

Write-Output "=== FALCON NETWORK LAB: NIC ADVANCED PROPERTY OPTIMIZATION ==="
Write-Output ""
Write-Output "This will try to adjust NIC advanced properties where present:"
Write-Output "  - Interrupt Moderation         -> Disabled"
Write-Output "  - Interrupt Moderation Rate   -> Off/Disabled"
Write-Output "  - Energy Efficient Ethernet   -> Disabled"
Write-Output "  - Green Ethernet / Power Save -> Disabled"
Write-Output ""
Write-Output "Drivers differ; Falcon only changes properties that exist and logs each change."
Write-Output ""

try {
  $nics = Get-NetAdapter -Physical -ErrorAction SilentlyContinue
  if (-not $nics) {
    Write-Output "No physical network adapters found."
  } else {
    foreach ($nic in $nics) {
      Write-Output ("Adapter: {0} | {1} | Status: {2}" -f $nic.Name, $nic.InterfaceDescription, $nic.Status)
      try {
        $props = Get-NetAdapterAdvancedProperty -Name $nic.Name -ErrorAction SilentlyContinue
      } catch {
        Write-Output "  Failed to query advanced properties."
        continue
      }

      foreach ($p in $props) {
        $dn = $p.DisplayName
        $dv = $p.DisplayValue

        # Interrupt Moderation
        if ($dn -eq "Interrupt Moderation" -or $dn -eq "Interrupt Moderation Rate") {
          if ($dv -ne "Disabled" -and $dv -ne "Off") {
            try {
              Set-NetAdapterAdvancedProperty -Name $nic.Name -DisplayName $dn -DisplayValue "Disabled" -NoRestart -ErrorAction SilentlyContinue
              Write-Output ("  Set {0} -> Disabled" -f $dn)
            } catch {
              Write-Output ("  Failed to set {0} on {1}: {2}" -f $dn, $nic.Name, $_.Exception.Message)
            }
          } else {
            Write-Output ("  {0} already Disabled/Off" -f $dn)
          }
        }

        # Energy Efficient Ethernet / Green Ethernet
        if ($dn -match "Energy Efficient Ethernet" -or $dn -match "Green Ethernet" -or $dn -match "Power Saving" -or $dn -match "Power Save Mode") {
          if ($dv -ne "Disabled" -and $dv -ne "Off") {
            try {
              Set-NetAdapterAdvancedProperty -Name $nic.Name -DisplayName $dn -DisplayValue "Disabled" -NoRestart -ErrorAction SilentlyContinue
              Write-Output ("  Set {0} -> Disabled" -f $dn)
            } catch {
              Write-Output ("  Failed to set {0} on {1}: {2}" -f $dn, $nic.Name, $_.Exception.Message)
            }
          } else {
            Write-Output ("  {0} already Disabled/Off" -f $dn)
          }
        }
      }

      Write-Output ""
    }
  }
} catch {
  Write-Output ("NIC optimization failed: {0}" -f $_.Exception.Message)
}

Write-Output "Note: Some NIC drivers require a reboot or link reset to fully apply new advanced properties."
Write-Output "=== END OF NIC ADVANCED PROPERTY OPTIMIZATION ==="

Write-Output "=== FALCON NETWORK LAB: REAL-TIME LATENCY LOG (TEXT) ==="
Write-Output ""
Write-Output "This will ping a target repeatedly and log timestamp + latency to a CSV file."
Write-Output "You can graph this later in Excel or any plotting tool."
Write-Output ""

$target = "8.8.8.8"
$count = 120
$delayMs = 500

$logsDir = Join-Path $PSScriptRoot "..\logs"
try {
  if (-not (Test-Path $logsDir)) {
    New-Item -ItemType Directory -Path $logsDir -Force | Out-Null
  }
} catch {}

$timestamp = Get-Date -Format "yyyyMMdd_HHmmss"
$logFile = Join-Path $logsDir ("falcon_latency_log_{0}.csv" -f $timestamp)

Write-Output ("Target: {0}" -f $target)
Write-Output ("Samples: {0}, Interval: {1} ms" -f $count, $delayMs)
Write-Output ("Log file: {0}" -f $logFile)
Write-Output ""

"Index,UtcTime,LatencyMs,Status" | Out-File -FilePath $logFile -Encoding UTF8 -Force

for ($i = 1; $i -le $count; $i++) {
  $now = (Get-Date).ToUniversalTime().ToString("o")
  try {
    $res = Test-Connection -ComputerName $target -Count 1 -ErrorAction SilentlyContinue
    if ($res) {
      $lat = [math]::Round($res.ResponseTime,2)
      Write-Output ("[{0}/{1}] {2} ms" -f $i, $count, $lat)
      ("{0},{1},{2},OK" -f $i, $now, $lat) | Out-File -FilePath $logFile -Append -Encoding UTF8
    } else {
      Write-Output ("[{0}/{1}] timeout" -f $i, $count)
      ("{0},{1},,TIMEOUT" -f $i, $now) | Out-File -FilePath $logFile -Append -Encoding UTF8
    }
  } catch {
    Write-Output ("[{0}/{1}] error: {2}" -f $i, $count, $_.Exception.Message)
    ("{0},{1},,ERROR" -f $i, $now) | Out-File -FilePath $logFile -Append -Encoding UTF8
  }
  Start-Sleep -Milliseconds $delayMs
}

Write-Output ""
Write-Output "Latency log complete. Open the CSV in a graphing tool for a visual latency graph."
Write-Output "=== END OF REAL-TIME LATENCY LOG ==="
