steps.command:
$runner = Join-Path $PSScriptRoot "processlab-run.ps1"
if (Test-Path $runner) {
  & $runner -Mode 'safe'
} else {
  Write-Host "ProcessLab runner not found."
}

$runner = Join-Path $PSScriptRoot "processlab-run.ps1"
if (Test-Path $runner) {
  & $runner -Mode 'competitive'
} else {
  Write-Host "ProcessLab runner not found."
}

$runner = Join-Path $PSScriptRoot "processlab-run.ps1"
if (Test-Path $runner) {
  & $runner -Mode 'extreme'
} else {
  Write-Host "ProcessLab runner not found."
}

Write-Output "=== FALCON SERVICE IMPACT LAB: BASELINE SNAPSHOT ==="
Write-Output ""
Write-Output "Capturing a baseline performance snapshot (CPU, DPC/ISR %, RAM, process count, top processes)."
Write-Output "Run this BEFORE disabling services, then run the AFTER snapshot and compare."
Write-Output ""

$logsDir = Join-Path $PSScriptRoot "..\logs"
try {
  if (-not (Test-Path $logsDir)) {
    New-Item -ItemType Directory -Path $logsDir -Force | Out-Null
  }
} catch {
  Write-Output "Failed to create logs directory."
}

$timestamp = Get-Date -Format "yyyyMMdd_HHmmss"
$snapFile = Join-Path $logsDir ("service_baseline_{0}.json" -f $timestamp)

# Collect counters
$data = [ordered]@{}
$data.TimestampUtc = (Get-Date).ToUniversalTime().ToString("o")

try {
  $cpu = Get-Counter '\Processor(_Total)\% Processor Time' -ErrorAction SilentlyContinue
  if ($cpu) { $data.CpuPercent = [math]::Round($cpu.CounterSamples[0].CookedValue, 2) }
} catch {}
try {
  $dpc = Get-Counter '\Processor(_Total)\% DPC Time' -ErrorAction SilentlyContinue
  if ($dpc) { $data.DpcPercent = [math]::Round($dpc.CounterSamples[0].CookedValue, 3) }
} catch {}
try {
  $isr = Get-Counter '\Processor(_Total)\% Interrupt Time' -ErrorAction SilentlyContinue
  if ($isr) { $data.IsrPercent = [math]::Round($isr.CounterSamples[0].CookedValue, 3) }
} catch {}

try {
  $os = Get-CimInstance Win32_OperatingSystem
  if ($os) {
    $total = [double]$os.TotalVisibleMemorySize
    $free  = [double]$os.FreePhysicalMemory
    $usedMB = ($total - $free) / 1024.0
    $totalMB = $total / 1024.0
    $data.RamUsedMB = [math]::Round($usedMB, 1)
    $data.RamTotalMB = [math]::Round($totalMB, 1)
    $data.RamUsedPercent = [math]::Round(($usedMB / $totalMB) * 100.0, 1)
  }
} catch {}

try {
  $procs = Get-Process -ErrorAction SilentlyContinue
  if ($procs) {
    $data.ProcessCount = $procs.Count
    $top = $procs | Sort-Object CPU -Descending | Select-Object -First 10 | ForEach-Object {
      [ordered]@{
        Name   = $_.ProcessName
        Id     = $_.Id
        CPU    = $_.CPU
        WS_MB  = [math]::Round(($_.WorkingSet64 / 1MB), 1)
      }
    }
    $data.TopProcesses = $top
  }
} catch {}

$json = $data | ConvertTo-Json -Depth 4
$json | Out-File -FilePath $snapFile -Encoding UTF8 -Force

Write-Output ("Baseline snapshot saved to: {0}" -f $snapFile)
Write-Output "Now: disable the services you want to test, then run the 'AFTER Snapshot & Compare' action."
Write-Output "=== END OF BASELINE SNAPSHOT ==="

Write-Output "=== FALCON SERVICE IMPACT LAB: AFTER SNAPSHOT & COMPARE ==="
Write-Output ""
Write-Output "Capturing a new snapshot and comparing against the latest baseline."
Write-Output ""

$logsDir = Join-Path $PSScriptRoot "..\logs"
if (-not (Test-Path $logsDir)) {
  Write-Output "No logs directory found. Run the baseline snapshot first."
  return
}

# Find latest baseline file
$baselineFiles = Get-ChildItem $logsDir -Filter "service_baseline_*.json" -ErrorAction SilentlyContinue | Sort-Object LastWriteTime -Descending
if (-not $baselineFiles -or $baselineFiles.Count -eq 0) {
  Write-Output "No baseline snapshot found. Run the baseline snapshot before using this."
  return
}

$baselineFile = $baselineFiles[0].FullName
Write-Output ("Using baseline: {0}" -f $baselineFile)

# Load baseline JSON
try {
  $baselineJson = Get-Content $baselineFile -Raw -ErrorAction SilentlyContinue
  $baseline = $baselineJson | ConvertFrom-Json -ErrorAction SilentlyContinue
} catch {
  Write-Output "Failed to parse baseline JSON."
  return
}

# Capture current snapshot
$timestamp = Get-Date -Format "yyyyMMdd_HHmmss"
$afterFile = Join-Path $logsDir ("service_after_{0}.json" -f $timestamp)

$data = [ordered]@{}
$data.TimestampUtc = (Get-Date).ToUniversalTime().ToString("o")

try {
  $cpu = Get-Counter '\Processor(_Total)\% Processor Time' -ErrorAction SilentlyContinue
  if ($cpu) { $data.CpuPercent = [math]::Round($cpu.CounterSamples[0].CookedValue, 2) }
} catch {}
try {
  $dpc = Get-Counter '\Processor(_Total)\% DPC Time' -ErrorAction SilentlyContinue
  if ($dpc) { $data.DpcPercent = [math]::Round($dpc.CounterSamples[0].CookedValue, 3) }
} catch {}
try {
  $isr = Get-Counter '\Processor(_Total)\% Interrupt Time' -ErrorAction SilentlyContinue
  if ($isr) { $data.IsrPercent = [math]::Round($isr.CounterSamples[0].CookedValue, 3) }
} catch {}

try {
  $os = Get-CimInstance Win32_OperatingSystem
  if ($os) {
    $total = [double]$os.TotalVisibleMemorySize
    $free  = [double]$os.FreePhysicalMemory
    $usedMB = ($total - $free) / 1024.0
    $totalMB = $total / 1024.0
    $data.RamUsedMB = [math]::Round($usedMB, 1)
    $data.RamTotalMB = [math]::Round($totalMB, 1)
    $data.RamUsedPercent = [math]::Round(($usedMB / $totalMB) * 100.0, 1)
  }
} catch {}

try {
  $procs = Get-Process -ErrorAction SilentlyContinue
  if ($procs) {
    $data.ProcessCount = $procs.Count
  }
} catch {}

$jsonAfter = $data | ConvertTo-Json -Depth 4
$jsonAfter | Out-File -FilePath $afterFile -Encoding UTF8 -Force

Write-Output ("After snapshot saved to: {0}" -f $afterFile)
Write-Output ""

# Compare key metrics
function Print-Diff($label, $baseVal, $afterVal, $format = "N2") {
  if ($null -eq $baseVal -or $null -eq $afterVal) {
    return
  }
  $delta = $afterVal - $baseVal
  $sign = if ($delta -gt 0) { "+" } elseif ($delta -lt 0) { "-" } else { "±" }
  Write-Output ("{0}: Baseline={1}, After={2}, Delta={3}{4}" -f $label, [string]::Format("{0:"+$format+"}",$baseVal), [string]::Format("{0:"+$format+"}",$afterVal), $sign, [string]::Format("{0:"+$format+"}",[math]::Abs($delta)))
}

Write-Output "=== Metric Comparison ==="
Print-Diff "CPU % (_Total)"    $baseline.CpuPercent     $data.CpuPercent     "N2"
Print-Diff "DPC % (_Total)"    $baseline.DpcPercent     $data.DpcPercent     "N3"
Print-Diff "ISR % (_Total)"    $baseline.IsrPercent     $data.IsrPercent     "N3"
Print-Diff "RAM Used MB"       $baseline.RamUsedMB      $data.RamUsedMB      "N1"
Print-Diff "RAM Used %"        $baseline.RamUsedPercent $data.RamUsedPercent "N1"
Print-Diff "Process Count"     $baseline.ProcessCount   $data.ProcessCount   "N0"

Write-Output ""
Write-Output "Heuristic notes:"
Write-Output "  - Lower CPU%, DPC%, ISR% and RAM usage after disabling services is good."
Write-Output "  - If CPU or DPC/ISR got worse, the changes likely did not help or increased overhead."
Write-Output ""
Write-Output "Use this as a data point while testing different service sets; it does not auto-disable services by itself."
Write-Output "=== END OF AFTER SNAPSHOT & COMPARE ==="

Write-Output "=== FALCON SERVICE IMPACT LAB: SERVICE & PROCESS VIEW ==="
Write-Output ""
Write-Output "Listing running services and top processes to help you decide what to target."
Write-Output ""

try {
  $svc = Get-Service | Where-Object { $_.Status -eq 'Running' } | Sort-Object DisplayName
  Write-Output ("Running services: {0}" -f $svc.Count)
  foreach ($s in $svc) {
    Write-Output ("  {0} ({1}) - Status: {2}" -f $s.DisplayName, $s.Name, $s.Status)
  }
} catch {
  Write-Output "Failed to query services."
}

Write-Output ""
Write-Output "Top 15 processes by CPU time:"
try {
  $procs = Get-Process -ErrorAction SilentlyContinue
  if ($procs) {
    $top = $procs | Sort-Object CPU -Descending | Select-Object -First 15
    foreach ($p in $top) {
      Write-Output ("  {0} (PID {1}) CPU={2} WS={3} MB" -f $p.ProcessName, $p.Id, $p.CPU, [math]::Round($p.WorkingSet64/1MB,1))
    }
  }
} catch {
  Write-Output "Failed to query process list."
}

Write-Output ""
Write-Output "Use this listing to choose candidates for Falcon's existing process/service disable presets."
Write-Output "=== END OF SERVICE & PROCESS VIEW ==="
