steps.command:
$base = Join-Path $env:LOCALAPPDATA 'VALORANT\Saved\Config'
if (!(Test-Path -LiteralPath $base)) {
  Write-Output 'Valorant config root not found at expected path.';
  exit 0
}

$files = Get-ChildItem -Path $base -Recurse -Filter 'GameUserSettings.ini' -ErrorAction SilentlyContinue
if (-not $files) {
  Write-Output 'No GameUserSettings.ini found under Valorant config.';
  exit 0
}

foreach ($f in $files) {
  try {
    $content = Get-Content -LiteralPath $f.FullName -Raw -ErrorAction SilentlyContinue
    if (-not $content) { continue }

    $replacements = @{
      'ResolutionSizeX='     = 'ResolutionSizeX=1280'
      'ResolutionSizeY='     = 'ResolutionSizeY=960'
      'DesiredScreenWidth='  = 'DesiredScreenWidth=1280'
      'DesiredScreenHeight=' = 'DesiredScreenHeight=960'
    }

    foreach ($k in $replacements.Keys) {
      $replacement = $replacements[$k]
      if ($content -match [regex]::Escape($k)) {
        $content = [regex]::Replace($content, [regex]::Escape($k) + '\d+', $replacement)
      } else {
        $content += "`n" + $replacement
      }
    }

    Set-Content -LiteralPath $f.FullName -Value $content -Encoding ASCII
  } catch {
    Write-Output ('Failed to touch Valorant config: {0}' -f $f.FullName)
  }
}

Write-Output 'Applied 1280x960-style stretched preset for Valorant (config files updated where found).'

$name = 'DiagTrack'
Write-Output ("[Hardcore] Disabling service: DiagTrack")
try {
  $svc = Get-Service -Name $name -ErrorAction SilentlyContinue
  if ($null -ne $svc) {
    if ($svc.Status -eq 'Running') {
      Stop-Service -Name $name -Force -ErrorAction SilentlyContinue
    }
    Set-Service -Name $name -StartupType Disabled -ErrorAction SilentlyContinue
  } else {
    Write-Output ("Service not found: DiagTrack")
  }
} catch {
  Write-Output ("Failed to disable service DiagTrack: " + $_.Exception.Message)
}

