try {
  $runKey = 'HKCU:\Software\Microsoft\Windows\CurrentVersion\Run'
  Remove-ItemProperty -Path $runKey -Name 'OneDrive' -ErrorAction SilentlyContinue
} catch {}
try {
  Get-Process OneDrive -ErrorAction SilentlyContinue | Stop-Process -Force -ErrorAction SilentlyContinue
} catch {}
Write-Output 'Disabled OneDrive auto-start and stopped running instances.'

try {
  New-Item -Path 'HKCU:\Software\Microsoft\Windows\CurrentVersion\Feeds' -Force | Out-Null
  New-ItemProperty -Path 'HKCU:\Software\Microsoft\Windows\CurrentVersion\Feeds' -Name 'ShellFeedsTaskbarViewMode' -Value 2 -PropertyType DWord -Force | Out-Null
  Write-Output 'Set ShellFeedsTaskbarViewMode=2 (disable taskbar news & interests).'
} catch {
  Write-Output ("Failed to adjust news & interests: " + $_.Exception.Message)
}

try {
  Disable-ScheduledTask -TaskName '\Microsoft\Windows\Defrag\ScheduledDefrag' -ErrorAction SilentlyContinue
  Write-Output 'Disabled ScheduledDefrag task.'
} catch {
  Write-Output ("Failed to disable ScheduledDefrag: " + $_.Exception.Message)
}

$path = 'HKLM:\SYSTEM\CurrentControlSet\Control\Session Manager\Memory Management\PrefetchParameters'
try {
  New-Item -Path $path -Force | Out-Null
  New-ItemProperty -Path $path -Name 'EnablePrefetcher' -Value 0 -PropertyType DWord -Force | Out-Null
  New-ItemProperty -Path $path -Name 'EnableSuperfetch' -Value 0 -PropertyType DWord -Force | Out-Null
  Write-Output 'Disabled Prefetcher and Superfetch via registry.'
} catch {
  Write-Output ("Failed to tweak PrefetchParameters: " + $_.Exception.Message)
}

$name = 'DoSvc'
Write-Output ("[Hardcore] Disabling update-related service: DoSvc")
try {
  $svc = Get-Service -Name $name -ErrorAction SilentlyContinue
  if ($svc) {
    if ($svc.Status -eq 'Running') {
      Stop-Service -Name $name -Force -ErrorAction SilentlyContinue
    }
    Set-Service -Name $name -StartupType Disabled -ErrorAction SilentlyContinue
  } else {
    Write-Output ("Service not found: DoSvc")
  }
} catch {
  Write-Output ("Failed to disable service DoSvc: " + $_.Exception.Message)
}

try {
  Set-MpPreference -DisableRealtimeMonitoring $true -ErrorAction SilentlyContinue
  Write-Output 'Requested DisableRealtimeMonitoring in Defender.'
} catch {
  Write-Output ("Failed to change Defender realtime monitoring: " + $_.Exception.Message)
}

try {
  bcdedit /set cfgsupportdisabled on
  Write-Output 'Attempted to disable CFG support via bcdedit (if supported). Reboot required.'
} catch {
  Write-Output ("Failed to adjust CFG boot setting: " + $_.Exception.Message)
}

$tasks=@("\Microsoft\Windows\Windows Defender\Windows Defender Scheduled Scan",
"\Microsoft\Windows\Windows Defender\Windows Defender Cache Maintenance",
"\Microsoft\Windows\Windows Defender\Windows Defender Verification",
"\Microsoft\Windows\Windows Defender\Windows Defender Cleanup");
foreach($t in $tasks){ schtasks /Change /TN $t /ENABLE | Out-Null; Write-Output ("Enabled: " + $t) }

Write-Output "Disabling pagefile (danger)..."
try {
  wmic computersystem where name="%computername%" set AutomaticManagedPagefile=False | Out-Null
  # Remove explicit pagefile settings (best-effort)
  wmic pagefileset delete | Out-Null
  Write-Output "Requested pagefile disable. Reboot required."
} catch {
  Write-Output ("Failed: {0}" -f $_.Exception.Message)
}

Write-Output "Re-enabling system-managed pagefile..."
try {
  wmic computersystem where name="%computername%" set AutomaticManagedPagefile=True | Out-Null
  Write-Output "System-managed pagefile re-enabled. Reboot recommended."
} catch {
  Write-Output ("Failed: {0}" -f $_.Exception.Message)
}

Write-Output "Re-enabling system-managed pagefile..."
try {
  wmic computersystem where name="%computername%" set AutomaticManagedPagefile=True | Out-Null
  Write-Output "System-managed pagefile re-enabled. Reboot recommended."
} catch {
  Write-Output ("Failed: {0}" -f $_.Exception.Message)
}
