steps.command:
$paths = @(
  "$env:ProgramFiles\FanControl\FanControl.exe",
  "$env:ProgramFiles (x86)\FanControl\FanControl.exe",
  "$env:LOCALAPPDATA\FanControl\FanControl.exe",
  "$env:USERPROFILE\Desktop\FanControl\FanControl.exe",
  "$env:USERPROFILE\Downloads\FanControl\FanControl.exe"
)
$exe = $paths | Where-Object { Test-Path $_ } | Select-Object -First 1
if ($exe) {
  Start-Process -FilePath $exe | Out-Null
  Write-Output "Launched: $exe"
} else {
  Write-Output "FanControl.exe not found in common paths."
  Write-Output "Tip: install to C:\Program Files\FanControl\FanControl.exe (or %LOCALAPPDATA%\FanControl\FanControl.exe)."
}
